sourcesContent:
import React, { useState } from 'react';
import { 
  ArrowLeft, 
  Phone, 
  Mail, 
  Building, 
  User, 
  Edit2, 
  Save,
  CheckCircle,
  ChevronRight,
  MessageSquare,
  Briefcase,
  Paperclip,
  Clock
} from 'lucide-react';

interface Lead {
  id: number;
  name: string;
  company: string;
  email: string;
  phone: string;
  status: string;
  value: number;
  source: string;
  created: string;
}

interface LeadDetailsProps {
  lead: Lead;
  onBack: () => void;
  onSave: (updatedLead: Lead) => void;
}

const LeadDetails: React.FC<LeadDetailsProps> = ({ lead, onBack, onSave }) => {
  const [activeTab, setActiveTab] = useState('overview');
  const [editingField, setEditingField] = useState<string | null>(null);
  const [editedLead, setEditedLead] = useState(lead);
  const [currentStage, setCurrentStage] = useState(lead.status);

  const pipelineStages = [
    { id: 'New', label: 'New', number: 1 },
    { id: 'Contacted', label: 'Contacted', number: 2 },
    { id: 'Qualified', label: 'Qualified', number: 3 },
    { id: 'Proposal', label: 'Proposal', number: 4 }
  ];

  const tabs = [
    { id: 'overview', label: 'Overview', icon: User },
    { id: 'deals', label: 'Deals', icon: Briefcase },
    { id: 'activity', label: 'Activity', icon: MessageSquare },
    { id: 'tasks', label: 'Tasks', icon: CheckCircle },
    { id: 'attachments', label: 'Attachments', icon: Paperclip },
    { id: 'timeline', label: 'Timeline', icon: Clock }
  ];

  const handleFieldEdit = (field: string) => {
    setEditingField(field);
  };

  const handleFieldSave = () => {
    setEditingField(null);
    onSave(editedLead);
  };

  const handleFieldChange = (field: string, value: string | number) => {
    setEditedLead(prev => ({ ...prev, [field]: value }));
  };

  const handleStageChange = (stage: string) => {
    setCurrentStage(stage);
    handleFieldChange('status', stage);
  };

  const getCurrentStageIndex = () => {
    return pipelineStages.findIndex(stage => stage.id === currentStage);
  };

  const getStageStatus = (stage: any, index: number) => {
    const currentIndex = getCurrentStageIndex();
    return {
      completed: index < currentIndex,
      active: index === currentIndex,
      inactive: index > currentIndex
    };
  };

  const styles = {
    container: {
      display: 'flex',
      flexDirection: 'column' as const,
      height: '100vh',
      backgroundColor: '#f8fafc',
      fontFamily: '"Inter", sans-serif'
    },
    leadCard: {
      backgroundColor: 'white',
      borderRadius: '16px',
      border: '1px solid #e2e8f0',
      margin: '11px 16px',
      padding: '21px',
      boxShadow: '0 1px 4px rgba(0, 0, 0, 0.1)',
      flexShrink: 0
    },
    leadHeader: {
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'space-between'
    },
    leadLeft: {
      display: 'flex',
      alignItems: 'center',
      gap: '21px'
    },
    companyLogo: {
      width: '64px',
      height: '64px',
      borderRadius: '11px',
      backgroundColor: '#3b82f6',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      color: 'white',
      fontSize: '24px',
      fontWeight: '600'
    },
    leadInfo: {
      display: 'flex',
      flexDirection: 'column' as const,
      gap: '5px'
    },
    leadTitle: {
      fontSize: '37px',
      fontWeight: '700',
      color: '#1e293b',
      margin: 0
    },
    companyInfo: {
      display: 'flex',
      alignItems: 'center',
      gap: '11px',
      fontSize: '21px',
      color: '#64748b',
      margin: 0
    },
    leadRight: {
      display: 'flex',
      alignItems: 'center',
      gap: '16px'
    },
    quickInfo: {
      display: 'flex',
      flexDirection: 'column' as const,
      gap: '5px',
      textAlign: 'right' as const
    },
    quickInfoItem: {
      display: 'flex',
      alignItems: 'center',
      gap: '11px',
      fontSize: '19px',
      color: '#475569'
    },
    content: {
      flex: 1,
      display: 'flex',
      flexDirection: 'column' as const,
      overflow: 'hidden',
      minHeight: 0
    },
    tabsCard: {
      backgroundColor: 'white',
      borderRadius: '16px',
      border: '1px solid #e2e8f0',
      margin: '0 16px 16px 16px',
      boxShadow: '0 1px 4px rgba(0, 0, 0, 0.1)',
      overflow: 'hidden',
      flex: 1,
      display: 'flex',
      flexDirection: 'column' as const,
      minHeight: 0
    },
    tabsContainer: {
      padding: '0',
      flexShrink: 0
    },
    tabs: {
      display: 'flex',
      gap: '0',
      borderBottom: '1px solid #e2e8f0'
    },
    tab: {
      display: 'flex',
      alignItems: 'center',
      gap: '11px',
      padding: '16px 21px',
      border: 'none',
      backgroundColor: 'transparent',
      color: '#64748b',
      cursor: 'pointer',
      fontSize: '19px',
      fontWeight: '500',
      borderBottom: '4px solid transparent',
      transition: 'color 0.2s, background-color 0.2s',
      position: 'relative' as const
    },
    tabActive: {
      color: '#3b82f6',
      borderBottom: '4px solid #3b82f6',
      backgroundColor: '#f8fafc'
    },
    tabContent: {
      padding: '21px',
      flex: 1,
      overflow: 'auto',
      minHeight: 0
    },
    mainContent: {
      flex: 1,
      padding: '0 16px 16px 16px',
      overflow: 'auto'
    },
    grid: {
      display: 'grid',
      gridTemplateColumns: '2fr 1fr',
      gap: '16px',
      height: '100%',
      minHeight: 0
    },
    scrollableColumn: {
      display: 'flex',
      flexDirection: 'column' as const,
      gap: '16px',
      overflow: 'auto',
      minHeight: 0,
      paddingRight: '5px'
    },
    card: {
      backgroundColor: 'white',
      borderRadius: '0',
      border: '1px solid #e2e8f0',
      overflow: 'hidden',
      boxShadow: '0 1px 4px rgba(0, 0, 0, 0.1)',
      marginBottom: '16px',
      flexShrink: 0
    },
    cardHeader: {
      padding: '21px',
      borderBottom: '1px solid #e2e8f0',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'space-between'
    },
    cardTitle: {
      fontSize: '21px',
      fontWeight: '600',
      color: '#1e293b',
      margin: 0
    },
    cardContent: {
      padding: '21px'
    },
    fieldGroup: {
      marginBottom: '21px'
    },
    fieldGroupLast: {
      marginBottom: '0'
    },
    fieldLabel: {
      display: 'block',
      fontSize: '17px',
      fontWeight: '500',
      color: '#374151',
      marginBottom: '8px'
    },
    fieldValue: {
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'space-between',
      padding: '13px 16px',
      backgroundColor: '#f8fafc',
      border: '1px solid #e2e8f0',
      borderRadius: '8px',
      fontSize: '19px',
      color: '#1e293b'
    },
    fieldInput: {
      width: '100%',
      padding: '13px 16px',
      border: '1px solid #d1d5db',
      borderRadius: '8px',
      fontSize: '19px',
      outline: 'none',
      transition: 'border-color 0.2s'
    },
    editButton: {
      padding: '5px',
      backgroundColor: 'transparent',
      border: 'none',
      color: '#6b7280',
      cursor: 'pointer',
      borderRadius: '5px',
      transition: 'color 0.2s'
    },
    saveButton: {
      display: 'flex',
      alignItems: 'center',
      gap: '11px',
      padding: '11px 21px',
      backgroundColor: '#3b82f6',
      color: 'white',
      border: 'none',
      borderRadius: '11px',
      cursor: 'pointer',
      fontSize: '19px',
      fontWeight: '500'
    },
    // Pipeline styles
    pipelineContainer: {
      backgroundColor: 'white',
      borderRadius: '16px',
      border: '1px solid #e2e8f0',
      margin: '0 16px 16px 16px',
      padding: '16px',
      boxShadow: '0 1px 4px rgba(0, 0, 0, 0.1)',
      flexShrink: 0
    },
    pipelineSteps: {
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'space-between'
    },
    pipelineStepsContainer: {
      display: 'flex',
      alignItems: 'center'
    },
    stageContainer: {
      display: 'flex',
      alignItems: 'center',
      position: 'relative' as const
    },
    stage: {
      display: 'flex',
      alignItems: 'center',
      padding: '11px 21px',
      fontSize: '19px',
      fontWeight: '500',
      transition: 'all 0.3s',
      position: 'relative' as const,
      cursor: 'pointer'
    },
    stageCompleted: {
      color: '#059669',
      backgroundColor: '#d1fae5'
    },
    stageActive: {
      color: '#1d4ed8',
      backgroundColor: '#dbeafe'
    },
    stageInactive: {
      color: '#6b7280',
      backgroundColor: '#f3f4f6'
    },
    stageArrow: {
      position: 'absolute' as const,
      right: 0,
      top: '50%',
      transform: 'translateY(-50%) translateX(50%)',
      zIndex: 10,
      width: 0,
      height: 0,
      borderTop: '21px solid transparent',
      borderBottom: '21px solid transparent',
      borderLeft: '11px solid'
    },
    stageConnector: {
      height: '43px',
      padding: '0 13px',
      display: 'flex',
      alignItems: 'center',
      transition: 'all 0.3s',
      position: 'relative' as const
    },
    stageConnectorCompleted: {
      backgroundColor: '#d1fae5'
    },
    stageConnectorActive: {
      backgroundColor: '#dbeafe'
    },
    stageConnectorInactive: {
      backgroundColor: '#f3f4f6'
    },
    markCompleteButton: {
      display: 'flex',
      alignItems: 'center',
      gap: '5px',
      padding: '8px 16px',
      backgroundColor: '#059669',
      color: 'white',
      fontSize: '16px',
      borderRadius: '8px',
      border: 'none',
      cursor: 'pointer',
      fontWeight: '500',
      transition: 'background-color 0.2s'
    }
  };

  const renderField = (label: string, field: keyof Lead, type: 'text' | 'email' | 'tel' | 'number' = 'text') => {
    const isEditing = editingField === field;
    const value = editedLead[field];

    return (
      <div style={styles.fieldGroup}>
        <label style={styles.fieldLabel}>{label}</label>
        {isEditing ? (
          <div style={{ display: 'flex', gap: '11px' }}>
            <input
              type={type}
              value={value}
              onChange={(e) => handleFieldChange(field, type === 'number' ? Number(e.target.value) : e.target.value)}
              style={styles.fieldInput}
            />
            <button style={styles.saveButton} onClick={handleFieldSave}>
              <Save size={21} />
            </button>
          </div>
        ) : (
          <div style={styles.fieldValue}>
            <span>{type === 'number' && field === 'value' ? `$${Number(value).toLocaleString()}` : value}</span>
            <button style={styles.editButton} onClick={() => handleFieldEdit(field)}>
              <Edit2 size={21} />
            </button>
          </div>
        )}
      </div>
    );
  };

  const renderPipelineView = () => {
    const currentIndex = getCurrentStageIndex();
    const isLastStage = currentIndex === pipelineStages.length - 1;

    return (
      <div style={styles.pipelineContainer}>
        <div style={styles.pipelineSteps}>
          <div style={styles.pipelineStepsContainer}>
            {pipelineStages.map((stage, index) => {
              const status = getStageStatus(stage, index);

              return (
                <React.Fragment key={stage.id}>
                  <div style={styles.stageContainer}>
                    <div
                      style={{
                        ...styles.stage,
                        ...(status.completed ? styles.stageCompleted : {}),
                        ...(status.active ? styles.stageActive : {}),
                        ...(status.inactive ? styles.stageInactive : {})
                      }}
                      onClick={() => handleStageChange(stage.id)}
                    >
                      {status.completed && <CheckCircle size={21} style={{ marginRight: '11px' }} />}
                      {stage.label}

                      {/* Arrow shape */}
                      {index < pipelineStages.length - 1 && (
                        <div
                          style={{
                            ...styles.stageArrow,
                            borderLeftColor: status.completed ? '#d1fae5' : 
                                           status.active ? '#dbeafe' : '#f3f4f6'
                          }}
                        />
                      )}
                    </div>
                  </div>

                  {index < pipelineStages.length - 1 && (
                    <div
                      style={{
                        ...styles.stageConnector,
                        ...(status.completed ? styles.stageConnectorCompleted : {}),
                        ...(status.active ? styles.stageConnectorActive : {}),
                        ...(status.inactive ? styles.stageConnectorInactive : {})
                      }}
                    >
                      <ChevronRight
                        size={21}
                        color={
                          pipelineStages[index + 1] && 
                          (getStageStatus(pipelineStages[index + 1], index + 1).completed || 
                           getStageStatus(pipelineStages[index + 1], index + 1).active) 
                            ? '#1d4ed8' : '#9ca3af'
                        }
                      />
                    </div>
                  )}
                </React.Fragment>
              );
            })}
          </div>

          {isLastStage && (
            <button
              style={styles.markCompleteButton}
              onClick={() => handleStageChange('Closed')}
              onMouseEnter={(e) => {
                e.currentTarget.style.backgroundColor = '#047857';
              }}
              onMouseLeave={(e) => {
                e.currentTarget.style.backgroundColor = '#059669';
              }}
            >
              <CheckCircle size={16} />
              Mark Complete
            </button>
          )}
        </div>
      </div>
    );
  };

  const renderOverviewTab = () => (
    <div style={styles.grid}>
      <div style={styles.scrollableColumn}>
        {/* Contact Information */}
        <div style={styles.card}>
          <div style={styles.cardHeader}>
            <h3 style={styles.cardTitle}>Contact Information</h3>
          </div>
          <div style={styles.cardContent}>
            <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '16px' }}>
              <div style={styles.fieldGroup}>
                <label style={styles.fieldLabel}>First Name</label>
                <div style={styles.fieldValue}>
                  <span>John</span>
                  <button style={styles.editButton}>
                    <Edit2 size={21} />
                  </button>
                </div>
              </div>
              <div style={styles.fieldGroup}>
                <label style={styles.fieldLabel}>Last Name</label>
                <div style={styles.fieldValue}>
                  <span>Smith</span>
                  <button style={styles.editButton}>
                    <Edit2 size={21} />
                  </button>
                </div>
              </div>
            </div>
            {renderField('Email', 'email', 'email')}
            {renderField('Phone', 'phone', 'tel')}
            <div style={styles.fieldGroupLast}>
              <label style={styles.fieldLabel}>Title</label>
              <div style={styles.fieldValue}>
                <span>CEO</span>
                <button style={styles.editButton}>
                  <Edit2 size={21} />
                </button>
              </div>
            </div>
          </div>
        </div>

        {/* Account Information */}
        <div style={styles.card}>
          <div style={styles.cardHeader}>
            <h3 style={styles.cardTitle}>Account Information</h3>
          </div>
          <div style={styles.cardContent}>
            {renderField('Company Name', 'company')}
            <div style={styles.fieldGroup}>
              <label style={styles.fieldLabel}>Industry</label>
              <div style={styles.fieldValue}>
                <span>Technology</span>
                <button style={styles.editButton}>
                  <Edit2 size={21} />
                </button>
              </div>
            </div>
            <div style={styles.fieldGroup}>
              <label style={styles.fieldLabel}>Website</label>
              <div style={styles.fieldValue}>
                <span>www.techcorp.com</span>
                <button style={styles.editButton}>
                  <Edit2 size={21} />
                </button>
              </div>
            </div>
            <div style={styles.fieldGroupLast}>
              <label style={styles.fieldLabel}>No. of Employees</label>
              <div style={styles.fieldValue}>
                <span>50-100</span>
                <button style={styles.editButton}>
                  <Edit2 size={21} />
                </button>
              </div>
            </div>
          </div>
        </div>

        {/* Address Information */}
        <div style={styles.card}>
          <div style={styles.cardHeader}>
            <h3 style={styles.cardTitle}>Address Information</h3>
          </div>
          <div style={styles.cardContent}>
            <div style={styles.fieldGroup}>
              <label style={styles.fieldLabel}>Street Address</label>
              <div style={styles.fieldValue}>
                <span>123 Business Avenue</span>
                <button style={styles.editButton}>
                  <Edit2 size={21} />
                </button>
              </div>
            </div>
            <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '16px' }}>
              <div style={styles.fieldGroup}>
                <label style={styles.fieldLabel}>City</label>
                <div style={styles.fieldValue}>
                  <span>New York</span>
                  <button style={styles.editButton}>
                    <Edit2 size={21} />
                  </button>
                </div>
              </div>
              <div style={styles.fieldGroup}>
                <label style={styles.fieldLabel}>Landmark (Optional)</label>
                <div style={styles.fieldValue}>
                  <span>Near Central Park</span>
                  <button style={styles.editButton}>
                    <Edit2 size={21} />
                  </button>
                </div>
              </div>
            </div>
            <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr 1fr', gap: '16px', marginBottom: '0' }}>
              <div style={styles.fieldGroup}>
                <label style={styles.fieldLabel}>State/Province</label>
                <div style={styles.fieldValue}>
                  <span>NY</span>
                  <button style={styles.editButton}>
                    <Edit2 size={21} />
                  </button>
                </div>
              </div>
              <div style={styles.fieldGroup}>
                <label style={styles.fieldLabel}>Country</label>
                <div style={styles.fieldValue}>
                  <span>United States</span>
                  <button style={styles.editButton}>
                    <Edit2 size={21} />
                  </button>
                </div>
              </div>
              <div style={styles.fieldGroup}>
                <label style={styles.fieldLabel}>Postal Code</label>
                <div style={styles.fieldValue}>
                  <span>10001</span>
                  <button style={styles.editButton}>
                    <Edit2 size={21} />
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Additional Information */}
        <div style={styles.card}>
          <div style={styles.cardHeader}>
            <h3 style={styles.cardTitle}>Additional Information</h3>
          </div>
          <div style={styles.cardContent}>
            <div style={styles.fieldGroupLast}>
              <label style={styles.fieldLabel}>Description</label>
              <div style={styles.fieldValue}>
                <span>High-potential lead from technology sector. Interested in enterprise solutions and has budget approval authority. Previous interactions have been positive and they've expressed strong interest in our services.</span>
                <button style={styles.editButton}>
                  <Edit2 size={21} />
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>

      <div style={styles.card}>
        <div style={styles.cardHeader}>
          <h3 style={styles.cardTitle}>Widgets</h3>
        </div>
        <div style={styles.cardContent}>
          <p style={{ color: '#6b7280', textAlign: 'center', padding: '20px' }}>
            Widgets and lead summary will be implemented here.
          </p>
        </div>
      </div>
    </div>
  );

  const renderDealsTab = () => (
    <div style={styles.card}>
      <div style={styles.cardHeader}>
        <h3 style={styles.cardTitle}>Deals & Opportunities</h3>
      </div>
      <div style={styles.cardContent}>
        <p style={{ color: '#6b7280', textAlign: 'center', padding: '40px' }}>
          Deals and opportunities related to this lead will be displayed here.
        </p>
      </div>
    </div>
  );

  const renderActivityTab = () => (
    <div style={styles.card}>
      <div style={styles.cardHeader}>
        <h3 style={styles.cardTitle}>Activity Timeline</h3>
      </div>
      <div style={styles.cardContent}>
        <p style={{ color: '#6b7280', textAlign: 'center', padding: '40px' }}>
          Activity tracking will be implemented here. This will show emails, calls, meetings, and other interactions with this lead.
        </p>
      </div>
    </div>
  );

  const renderTasksTab = () => (
    <div style={styles.card}>
      <div style={styles.cardHeader}>
        <h3 style={styles.cardTitle}>Tasks & Follow-ups</h3>
      </div>
      <div style={styles.cardContent}>
        <p style={{ color: '#6b7280', textAlign: 'center', padding: '40px' }}>
          Tasks and follow-ups related to this lead will be displayed here.
        </p>
      </div>
    </div>
  );

  const renderAttachmentsTab = () => (
    <div style={styles.card}>
      <div style={styles.cardHeader}>
        <h3 style={styles.cardTitle}>Documents & Attachments</h3>
      </div>
      <div style={styles.cardContent}>
        <p style={{ color: '#6b7280', textAlign: 'center', padding: '40px' }}>
          Document management will be implemented here. This will show proposals, contracts, and other files related to this lead.
        </p>
      </div>
    </div>
  );

  const renderTimelineTab = () => (
    <div style={styles.card}>
      <div style={styles.cardHeader}>
        <h3 style={styles.cardTitle}>Timeline & History</h3>
      </div>
      <div style={styles.cardContent}>
        <p style={{ color: '#6b7280', textAlign: 'center', padding: '40px' }}>
          Complete timeline and history of interactions with this lead will be displayed here.
        </p>
      </div>
    </div>
  );

  const renderTabContent = () => {
    switch (activeTab) {
      case 'overview':
        return renderOverviewTab();
      case 'deals':
        return renderDealsTab();
      case 'activity':
        return renderActivityTab();
      case 'tasks':
        return renderTasksTab();
      case 'attachments':
        return renderAttachmentsTab();
      case 'timeline':
        return renderTimelineTab();
      default:
        return renderOverviewTab();
    }
  };

  return (
    <div style={styles.container}>
      {/* Lead Information Card */}
      <div style={styles.leadCard}>
        <div style={styles.leadHeader}>
          <div style={styles.leadLeft}>
            <div style={styles.companyLogo}>
              {lead.company.charAt(0).toUpperCase()}
            </div>
            <div style={styles.leadInfo}>
              <h1 style={styles.leadTitle}>{lead.name}</h1>
              <div style={styles.companyInfo}>
                <Building size={21} />
                <span>{lead.company}</span>
              </div>
            </div>
          </div>
          <div style={styles.leadRight}>
            <div style={styles.quickInfo}>
              <div style={styles.quickInfoItem}>
                <Phone size={21} />
                <span>{lead.phone}</span>
              </div>
              <div style={styles.quickInfoItem}>
                <Mail size={21} />
                <span>{lead.email}</span>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Pipeline Card */}
      {renderPipelineView()}

      {/* Tabs Card */}
      <div style={styles.tabsCard}>
        <div style={styles.tabsContainer}>
          <div style={styles.tabs}>
            {tabs.map((tab) => {
              const IconComponent = tab.icon;
              return (
                <button
                  key={tab.id}
                  style={{
                    ...styles.tab,
                    ...(activeTab === tab.id ? styles.tabActive : {})
                  }}
                  onClick={() => setActiveTab(tab.id)}
                  onMouseEnter={(e) => {
                    if (activeTab !== tab.id) {
                      e.currentTarget.style.backgroundColor = '#f8fafc';
                    }
                  }}
                  onMouseLeave={(e) => {
                    if (activeTab !== tab.id) {
                      e.currentTarget.style.backgroundColor = 'transparent';
                    }
                  }}
                >
                  <IconComponent size={21} />
                  {tab.label}
                </button>
              );
            })}
          </div>
        </div>
        <div style={styles.tabContent}>
          {renderTabContent()}
        </div>
      </div>
    </div>
  );
};

export default LeadDetails;
