import React, { useState, useEffect } from 'react';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
  PointElement,
  LineElement,
} from 'chart.js';
import { Bar, Pie, Line } from 'react-chartjs-2';
import {
  Menu,
  X,
  Plus,
  Users,
  Building,
  Phone,
  HeartHandshake,
  FileText,
  ShoppingCart,
  Package,
  Home,
  TrendingUp,
  Clock,
  Target,
  Mail,
  Calendar,
  Edit,
  Trash2,
  Eye,
  Settings,
  Bell,
  User,
  UserPlus,
  Building2,
  FileText as QuoteIcon,
  ShoppingBag,
  ChevronDown,
} from 'lucide-react';
import LeadsPage from './components/LeadsPage';
import { Lead } from './components/LeadsTable';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
  PointElement,
  LineElement
);

const App: React.FC = () => {
  const [sidebarCollapsed, setSidebarCollapsed] = useState(false);
  const [activeSection, setActiveSection] = useState('dashboard');
  const [showLeadModal, setShowLeadModal] = useState(false);
  const [showQuickActions, setShowQuickActions] = useState(false);
  const [showCreateLeadPage, setShowCreateLeadPage] = useState(false);
  const [selectedLead, setSelectedLead] = useState<Lead | null>(null);
  const [showLeadDetails, setShowLeadDetails] = useState(false);

  // State for editing fields in lead details
  const [editFields, setEditFields] = useState<{ [key: string]: boolean }>({});
  const [editLeadData, setEditLeadData] = useState<any>(null);

  // When opening lead details, initialize editLeadData
  useEffect(() => {
    if (showLeadDetails && selectedLead) {
      setEditLeadData({ ...selectedLead });
      setEditFields({});
    }
  }, [showLeadDetails, selectedLead]);

  const handleEditField = (field: string) => {
    setEditFields((prev) => ({ ...prev, [field]: true }));
  };

  const handleFieldChange = (field: string, value: string) => {
    setEditLeadData((prev: any) => ({ ...prev, [field]: value }));
  };

  const handleSaveLeadChanges = () => {
    // Save logic here (e.g., update leads array)
    setShowLeadDetails(false);
    setSelectedLead(null);
  };



  // Navigation items
  const navItems = [
    { id: 'dashboard', label: 'Dashboard', icon: Home },
    { id: 'leads', label: 'Leads', icon: Users },
    { id: 'accounts', label: 'Accounts', icon: Building },
    { id: 'contacts', label: 'Contacts', icon: Phone },
    { id: 'deals', label: 'Deals', icon: HeartHandshake },
    { id: 'quotes', label: 'Quotes', icon: FileText },
    { id: 'orders', label: 'Orders', icon: ShoppingCart },
    { id: 'products', label: 'Products', icon: Package },
  ];

  // Chart data
  const revenueData = {
    labels: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun'],
    datasets: [
      {
        label: 'Revenue',
        data: [45000, 52000, 48000, 61000, 55000, 67000],
        backgroundColor: 'rgba(59, 130, 246, 0.5)',
        borderColor: 'rgb(59, 130, 246)',
        borderWidth: 1,
      },
    ],
  };

  const pieData = {
    labels: ['New', 'Qualified', 'Proposal', 'Won', 'Lost'],
    datasets: [
      {
        data: [30, 25, 20, 15, 10],
        backgroundColor: [
          'rgb(59, 130, 246)',
          'rgb(16, 185, 129)',
          'rgb(245, 158, 11)',
          'rgb(34, 197, 94)',
          'rgb(239, 68, 68)',
        ],
      },
    ],
  };

  const lineData = {
    labels: ['Week 1', 'Week 2', 'Week 3', 'Week 4'],
    datasets: [
      {
        label: 'Leads Generated',
        data: [12, 19, 15, 25],
        borderColor: 'rgb(59, 130, 246)',
        backgroundColor: 'rgba(59, 130, 246, 0.2)',
      },
    ],
  };



  // Handle comprehensive lead form submission
  const handleSubmitCreateLead = (e: React.FormEvent) => {
    e.preventDefault();
    const lead = {
      id: leads.length + 1,
      name: `${createLeadForm.firstName} ${createLeadForm.lastName}`,
      company: createLeadForm.company,
      email: createLeadForm.email,
      phone: createLeadForm.phone,
      status: createLeadForm.status,
      value: parseInt(createLeadForm.value) || 0,
      source: createLeadForm.source,
      created: new Date().toISOString().split('T')[0],
    };
    setLeads([...leads, lead]);
    setCreateLeadForm({
      firstName: '',
      lastName: '',
      company: '',
      jobTitle: '',
      email: '',
      phone: '',
      mobile: '',
      website: '',
      status: 'New',
      value: '',
      source: '',
      industry: '',
      address: '',
      city: '',
      state: '',
      zipCode: '',
      country: '',
      description: '',
      notes: '',
      assignedTo: '',
      priority: 'Medium',
      tags: '',
    });
    setShowCreateLeadPage(false);
  };

  const handleSort = (field: string) => {
    if (sortField === field) {
      setSortDirection(sortDirection === 'asc' ? 'desc' : 'asc');
    } else {
      setSortField(field);
      setSortDirection('asc');
    }
  };

  const handleLeadClick = (lead: any) => {
    setSelectedLead(lead);
    setShowLeadDetails(true);
  };

  const handleBackToLeads = () => {
    setShowLeadDetails(false);
    setSelectedLead(null);
  };

  const styles: { [key: string]: React.CSSProperties } = {
    container: {
      display: 'flex',
      height: '100vh',
      fontFamily: '"Inter", sans-serif',
      backgroundColor: '#ffffff',
    },
    sidebar: {
      width: sidebarCollapsed ? '80px' : '280px',
      backgroundColor: '#1e40af',
      color: 'white',
      transition: 'width 0.3s ease',
      flexShrink: 0,
      borderRight: '1px solid #e5e7eb',
      display: 'flex',
      flexDirection: 'column' as const,
      alignItems: sidebarCollapsed ? 'center' : 'stretch',
    },
    sidebarHeader: {
      padding: '16px',
      borderBottom: '1px solid rgba(255, 255, 255, 0.1)',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'space-between',
      gap: '8px',
    },
    logo: {
      display: 'flex',
      alignItems: 'center',
      gap: '8px',
    },
    logoImg: {
      width: '32px',
      height: '32px',
      display: sidebarCollapsed ? 'none' : 'block',
    },
    logoText: {
      fontSize: '18px',
      fontWeight: 'bold',
      color: 'white',
      display: sidebarCollapsed ? 'none' : 'block',
      letterSpacing: '0.5px',
    },
    toggleButton: {
      background: 'none',
      border: 'none',
      color: 'white',
      cursor: 'pointer',
      padding: '6px',
      borderRadius: '4px',
      transition: 'background-color 0.2s',
    },
    navList: {
      listStyle: 'none',
      padding: '16px 0',
      margin: 0,
      flex: 1,
      display: 'flex',
      flexDirection: 'column' as React.CSSProperties['flexDirection'],
      alignItems: 'center' as React.CSSProperties['alignItems'],
    },
    navItem: {
      margin: '4px 0',
      width: '100%',
      display: 'flex',
      justifyContent: 'center',
    },
    navLink: {
      display: 'flex',
      alignItems: 'center',
      justifyContent: sidebarCollapsed ? 'center' : 'flex-start',
      padding: sidebarCollapsed ? '12px 0' : '10px 16px',
      color: 'white',
      textDecoration: 'none',
      transition: 'background-color 0.2s',
      cursor: 'pointer',
      backgroundColor: 'transparent',
      border: 'none',
      width: sidebarCollapsed ? '48px' : '100%',
      height: '48px',
      fontSize: '14px',
    },
    navLinkActive: {
      backgroundColor: 'rgba(255, 255, 255, 0.1)',
    },
    navIcon: {
