import React, { useState } from 'react';
import { 
  ArrowLeft, 
  Phone, 
  Mail, 
  Building, 
  User, 
  CheckCircle,
  ChevronRight,
  MessageSquare,
  Briefcase,
  Paperclip,
  Clock,
  UserPlus
} from 'lucide-react';

interface Lead {
  id: number;
  name: string;
  company: string;
  email: string;
  phone: string;
  status: string;
  value: number;
  source: string;
  created: string;
}

interface LeadDetailsProps {
  lead: Lead;
  onBack: () => void;
  onSave: (updatedLead: Lead) => void;
}

const LeadDetails: React.FC<LeadDetailsProps> = ({ lead, onBack, onSave }) => {
  const [activeTab, setActiveTab] = useState('overview');
  const [currentStage, setCurrentStage] = useState(lead.status);

  const pipelineStages = [
    { id: 'New', label: 'New', number: 1 },
    { id: 'Contacted', label: 'Contacted', number: 2 },
    { id: 'Qualified', label: 'Qualified', number: 3 },
    { id: 'Proposal', label: 'Proposal', number: 4 }
  ];

  const tabs = [
    { id: 'overview', label: 'Overview', icon: User },
    { id: 'deals', label: 'Deals', icon: Briefcase },
    { id: 'activity', label: 'Activity', icon: MessageSquare },
    { id: 'tasks', label: 'Tasks', icon: CheckCircle },
    { id: 'attachments', label: 'Attachments', icon: Paperclip },
    { id: 'timeline', label: 'Timeline', icon: Clock }
  ];

  const handleStageChange = (stage: string) => {
    setCurrentStage(stage);
  };

  const getCurrentStageIndex = () => {
    return pipelineStages.findIndex(stage => stage.id === currentStage);
  };

  const getStageStatus = (stage: any, index: number) => {
    const currentIndex = getCurrentStageIndex();
    return {
      completed: index < currentIndex,
      active: index === currentIndex,
      inactive: index > currentIndex
    };
  };

  const styles = {
    container: {
      display: 'flex',
      flexDirection: 'column' as const,
      height: '100vh',
      backgroundColor: '#f8fafc',
      fontFamily: '"Inter", sans-serif'
    },
    leadCard: {
      backgroundColor: 'white',
      borderRadius: '8px',
      border: '1px solid #e2e8f0',
      margin: '4px 8px',
      padding: '8px',
      boxShadow: '0 1px 2px rgba(0, 0, 0, 0.1)',
      flexShrink: 0
    },
    leadHeader: {
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'space-between'
    },
    leadLeft: {
      display: 'flex',
      flexDirection: 'row' as const,
      gap: '12px',
      height: '80px'
    },
    logoSection: {
      display: 'flex',
      flexDirection: 'column' as const,
      gap: '4px'
    },
    topRow: {
      display: 'flex',
      alignItems: 'flex-start',
      gap: '12px',
      height: '100%'
    },
    nameAndDetails: {
      display: 'flex',
      flexDirection: 'column' as const,
      justifyContent: 'center',
      height: '75%',
      gap: '2px'
    },
    companyBelowLogo: {
      display: 'flex',
      alignItems: 'center',
      gap: '6px',
      fontSize: '12px',
      color: '#64748b',
      margin: 0
    },
    bottomInfo: {
      display: 'flex',
      flexDirection: 'column' as const,
      gap: '2px'
    },
    companyLogo: {
      width: '40px',
      height: '40px',
      borderRadius: '6px',
      backgroundColor: '#3b82f6',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      color: 'white',
      fontSize: '16px',
      fontWeight: '600'
    },
    leadTitle: {
      fontSize: '32px',
      fontWeight: '700',
      color: '#1e293b',
      margin: 0,
      lineHeight: '40px',
      height: '40px',
      display: 'flex',
      alignItems: 'center'
    },
    titleInfo: {
      display: 'flex',
      alignItems: 'center',
      gap: '6px',
      fontSize: '14px',
      color: '#64748b',
      margin: 0
    },
    companyInfo: {
      display: 'flex',
      alignItems: 'center',
      gap: '6px',
      fontSize: '14px',
      color: '#64748b',
      margin: 0
    },
    phoneInfo: {
      display: 'flex',
      alignItems: 'center',
      gap: '6px',
      fontSize: '14px',
      color: '#64748b',
      margin: 0
    },
    leadRight: {
      display: 'flex',
      alignItems: 'center',
      gap: '10px'
    },
    convertButton: {
      display: 'flex',
      alignItems: 'center',
      gap: '8px',
      padding: '8px 16px',
      backgroundColor: '#16a34a',
      color: 'white',
      border: 'none',
      borderRadius: '6px',
      cursor: 'pointer',
      fontSize: '14px',
      fontWeight: '500',
      transition: 'background-color 0.2s'
    },
    content: {
      flex: 1,
      display: 'flex',
      flexDirection: 'column' as const,
      overflow: 'hidden',
      minHeight: 0
    },
    tabsCard: {
      backgroundColor: 'white',
      borderRadius: '8px',
      border: '1px solid #e2e8f0',
      margin: '0 8px 8px 8px',
      boxShadow: '0 1px 2px rgba(0, 0, 0, 0.1)',
      overflow: 'hidden',
      flex: 1,
      display: 'flex',
      flexDirection: 'column' as const,
      minHeight: 0
    },
    tabsContainer: {
      padding: '0',
      flexShrink: 0
    },
    tabs: {
      display: 'flex',
      gap: '0',
      borderBottom: '1px solid #e2e8f0'
    },
    tab: {
      display: 'flex',
      alignItems: 'center',
      gap: '6px',
      padding: '6px 12px',
      border: 'none',
      backgroundColor: 'transparent',
      color: '#64748b',
      cursor: 'pointer',
      fontSize: '13px',
      fontWeight: '500',
      borderBottom: '2px solid transparent',
      transition: 'color 0.2s, background-color 0.2s',
      position: 'relative' as const
    },
    tabActive: {
      color: '#3b82f6',
      borderBottom: '2px solid #3b82f6',
      backgroundColor: '#f8fafc'
    },
    tabContent: {
      padding: '8px',
      flex: 1,
      overflow: 'auto',
      minHeight: 0
    },
    mainContent: {
      flex: 1,
      padding: '0 8px 8px 8px',
      overflow: 'auto'
    },
    grid: {
      display: 'grid',
      gridTemplateColumns: '2fr 0.6fr',
      gap: '8px',
      height: '100%',
      minHeight: 0
    },
    scrollableColumn: {
      display: 'flex',
      flexDirection: 'column' as const,
      gap: '6px',
      overflow: 'auto',
      minHeight: 0,
      paddingRight: '3px'
    },
    card: {
      backgroundColor: 'white',
      borderRadius: '0',
      border: '1px solid #e2e8f0',
      overflow: 'hidden',
      boxShadow: '0 1px 2px rgba(0, 0, 0, 0.1)',
      marginBottom: '6px',
      flexShrink: 0
    },
    cardHeader: {
      padding: '8px 10px',
      borderBottom: '1px solid #e2e8f0',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'space-between'
    },
    cardTitle: {
      fontSize: '13px',
      fontWeight: '600',
      color: '#1e293b',
      margin: 0
    },
    cardContent: {
      padding: '8px 10px'
    },
    fieldGroup: {
      marginBottom: '8px'
    },
    fieldGroupLast: {
      marginBottom: '0'
    },
    fieldLabel: {
      display: 'block',
      fontSize: '11px',
      fontWeight: '500',
      color: '#374151',
      marginBottom: '4px'
    },
    fieldValueDisplay: {
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'space-between',
      padding: '6px 8px',
      backgroundColor: '#f8fafc',
      border: '1px solid #e2e8f0',
      borderRadius: '4px',
      fontSize: '13px',
      color: '#1e293b'
    },
    // Pipeline styles
    pipelineContainer: {
      backgroundColor: 'white',
      borderRadius: '8px',
      border: '1px solid #e2e8f0',
      margin: '0 8px 8px 8px',
      padding: '6px',
      boxShadow: '0 1px 2px rgba(0, 0, 0, 0.1)',
      flexShrink: 0
    },
    pipelineSteps: {
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'space-between'
    },
    pipelineStepsContainer: {
      display: 'flex',
      alignItems: 'center'
    },
    stageContainer: {
      display: 'flex',
      alignItems: 'center',
      position: 'relative' as const
    },
    stage: {
      display: 'flex',
      alignItems: 'center',
      padding: '6px 12px',
      fontSize: '13px',
      fontWeight: '500',
      transition: 'all 0.3s',
      position: 'relative' as const,
      cursor: 'pointer'
    },
    stageCompleted: {
      color: '#059669',
      backgroundColor: '#d1fae5'
    },
    stageActive: {
      color: '#1d4ed8',
      backgroundColor: '#dbeafe'
    },
    stageInactive: {
      color: '#6b7280',
      backgroundColor: '#f3f4f6'
    },
    stageArrow: {
      position: 'absolute' as const,
      right: 0,
      top: '50%',
      transform: 'translateY(-50%) translateX(50%)',
      zIndex: 10,
      width: 0,
      height: 0,
      borderTop: '14px solid transparent',
      borderBottom: '14px solid transparent',
      borderLeft: '7px solid'
    },
    stageConnector: {
      height: '28px',
      padding: '0 8px',
      display: 'flex',
      alignItems: 'center',
      transition: 'all 0.3s',
      position: 'relative' as const
    },
    stageConnectorCompleted: {
      backgroundColor: '#d1fae5'
    },
    stageConnectorActive: {
      backgroundColor: '#dbeafe'
    },
    stageConnectorInactive: {
      backgroundColor: '#f3f4f6'
    },
    markCompleteButton: {
      display: 'flex',
      alignItems: 'center',
      gap: '3px',
      padding: '4px 8px',
      backgroundColor: '#059669',
      color: 'white',
      fontSize: '11px',
      borderRadius: '4px',
      border: 'none',
      cursor: 'pointer',
      fontWeight: '500',
      transition: 'background-color 0.2s'
    }
  };

  const renderDisplayField = (label: string, value: string, isLast: boolean = false) => {
    return (
      <div style={isLast ? styles.fieldGroupLast : styles.fieldGroup}>
        <label style={styles.fieldLabel}>{label}</label>
        <div 
          style={styles.fieldValueDisplay}
          onMouseEnter={(e) => {
            e.currentTarget.style.backgroundColor = '#f1f5f9';
          }}
          onMouseLeave={(e) => {
            e.currentTarget.style.backgroundColor = '#f8fafc';
          }}
        >
          <span>{value}</span>
        </div>
      </div>
    );
  };

  const renderField = (label: string, field: keyof Lead, type: 'text' | 'email' | 'tel' | 'number' = 'text') => {
    const value = lead[field];

    return (
      <div style={styles.fieldGroup}>
        <label style={styles.fieldLabel}>{label}</label>
        <div 
          style={styles.fieldValueDisplay}
          onMouseEnter={(e) => {
            e.currentTarget.style.backgroundColor = '#f1f5f9';
          }}
          onMouseLeave={(e) => {
            e.currentTarget.style.backgroundColor = '#f8fafc';
          }}
        >
          <span>{type === 'number' && field === 'value' ? `$${Number(value).toLocaleString()}` : value}</span>
        </div>
      </div>
    );
  };

  const renderPipelineView = () => {
    const currentIndex = getCurrentStageIndex();
    const isLastStage = currentIndex === pipelineStages.length - 1;

    return (
      <div style={styles.pipelineContainer}>
        <div style={styles.pipelineSteps}>
          <div style={styles.pipelineStepsContainer}>
            {pipelineStages.map((stage, index) => {
              const status = getStageStatus(stage, index);

              return (
                <React.Fragment key={stage.id}>
                  <div style={styles.stageContainer}>
                    <div
                      style={{
                        ...styles.stage,
                        ...(status.completed ? styles.stageCompleted : {}),
                        ...(status.active ? styles.stageActive : {}),
                        ...(status.inactive ? styles.stageInactive : {})
                      }}
                      onClick={() => handleStageChange(stage.id)}
                    >
                      {status.completed && <CheckCircle size={14} style={{ marginRight: '6px' }} />}
                      {stage.label}

                      {/* Arrow shape */}
                      {index < pipelineStages.length - 1 && (
                        <div
                          style={{
                            ...styles.stageArrow,
                            borderLeftColor: status.completed ? '#d1fae5' : 
                                           status.active ? '#dbeafe' : '#f3f4f6'
                          }}
                        />
                      )}
                    </div>
                  </div>

                  {index < pipelineStages.length - 1 && (
                    <div
                      style={{
                        ...styles.stageConnector,
                        ...(status.completed ? styles.stageConnectorCompleted : {}),
                        ...(status.active ? styles.stageConnectorActive : {}),
                        ...(status.inactive ? styles.stageConnectorInactive : {})
                      }}
                    >
                      <ChevronRight
                        size={14}
                        color={
                          pipelineStages[index + 1] && 
                          (getStageStatus(pipelineStages[index + 1], index + 1).completed || 
                           getStageStatus(pipelineStages[index + 1], index + 1).active) 
                            ? '#1d4ed8' : '#9ca3af'
                        }
                      />
                    </div>
                  )}
                </React.Fragment>
              );
            })}
          </div>

          {isLastStage && (
            <button
              style={styles.markCompleteButton}
              onClick={() => handleStageChange('Closed')}
              onMouseEnter={(e) => {
                e.currentTarget.style.backgroundColor = '#047857';
              }}
              onMouseLeave={(e) => {
                e.currentTarget.style.backgroundColor = '#059669';
              }}
            >
              <CheckCircle size={11} />
              Mark Complete
            </button>
          )}
        </div>
      </div>
    );
  };

  const renderOverviewTab = () => (
    <div style={styles.grid}>
      <div style={styles.scrollableColumn}>
        {/* Contact Information */}
        <div style={styles.card}>
          <div style={styles.cardHeader}>
            <h3 style={styles.cardTitle}>Contact Information</h3>
          </div>
          <div style={styles.cardContent}>
            <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '10px' }}>
              {renderDisplayField('First Name', 'John')}
              {renderDisplayField('Last Name', 'Smith')}
            </div>
            {renderField('Email', 'email', 'email')}
            {renderField('Phone', 'phone', 'tel')}
            {renderDisplayField('Title', 'CEO', true)}
          </div>
        </div>

        {/* Account Information */}
        <div style={styles.card}>
          <div style={styles.cardHeader}>
            <h3 style={styles.cardTitle}>Account Information</h3>
          </div>
          <div style={styles.cardContent}>
            {renderField('Company Name', 'company')}
            {renderDisplayField('Industry', 'Technology')}
            {renderDisplayField('Website', 'www.techcorp.com')}
            {renderDisplayField('No. of Employees', '50-100', true)}
          </div>
        </div>

        {/* Address Information */}
        <div style={styles.card}>
          <div style={styles.cardHeader}>
            <h3 style={styles.cardTitle}>Address Information</h3>
          </div>
          <div style={styles.cardContent}>
            {/* Address Line 1 - Full width */}
            {renderDisplayField('Address Line 1 (Street Address)', '123 Business Avenue')}

            {/* Address Line 2 and Landmark - Two columns */}
            <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '10px' }}>
              {renderDisplayField('Address Line 2 (Area, Street, Sector, Village)', 'Downtown Business District')}
              {renderDisplayField('Landmark (optional)', 'Near Central Park')}
            </div>

            {/* City and State - Two columns */}
            <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '10px' }}>
              {renderDisplayField('City', 'New York')}
              {renderDisplayField('State', 'NY')}
            </div>

            {/* Country and Postal Index - Two columns */}
            <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '10px', marginBottom: '0' }}>
              {renderDisplayField('Country', 'United States')}
              {renderDisplayField('Postal Index', '10001', true)}
            </div>
          </div>
        </div>

        {/* Additional Information */}
        <div style={styles.card}>
          <div style={styles.cardHeader}>
            <h3 style={styles.cardTitle}>Additional Information</h3>
          </div>
          <div style={styles.cardContent}>
            {renderDisplayField('Description', 'High-potential lead from technology sector. Interested in enterprise solutions and has budget approval authority. Previous interactions have been positive and they\'ve expressed strong interest in our services.', true)}
          </div>
        </div>
      </div>

      <div style={styles.card}>
        <div style={styles.cardHeader}>
          <h3 style={styles.cardTitle}>Widgets</h3>
        </div>
        <div style={styles.cardContent}>
          <p style={{ color: '#6b7280', textAlign: 'center', padding: '10px' }}>
            Widgets and lead summary will be implemented here.
          </p>
        </div>
      </div>
    </div>
  );

  const renderDealsTab = () => (
    <div style={styles.card}>
      <div style={styles.cardHeader}>
        <h3 style={styles.cardTitle}>Deals & Opportunities</h3>
      </div>
      <div style={styles.cardContent}>
        <p style={{ color: '#6b7280', textAlign: 'center', padding: '20px' }}>
          Deals and opportunities related to this lead will be displayed here.
        </p>
      </div>
    </div>
  );

  const renderActivityTab = () => (
    <div style={styles.card}>
      <div style={styles.cardHeader}>
        <h3 style={styles.cardTitle}>Activity Timeline</h3>
      </div>
      <div style={styles.cardContent}>
        <p style={{ color: '#6b7280', textAlign: 'center', padding: '20px' }}>
          Activity tracking will be implemented here. This will show emails, calls, meetings, and other interactions with this lead.
        </p>
      </div>
    </div>
  );

  const renderTasksTab = () => (
    <div style={styles.card}>
      <div style={styles.cardHeader}>
        <h3 style={styles.cardTitle}>Tasks & Follow-ups</h3>
      </div>
      <div style={styles.cardContent}>
        <p style={{ color: '#6b7280', textAlign: 'center', padding: '20px' }}>
          Tasks and follow-ups related to this lead will be displayed here.
        </p>
      </div>
    </div>
  );

  const renderAttachmentsTab = () => (
    <div style={styles.card}>
      <div style={styles.cardHeader}>
        <h3 style={styles.cardTitle}>Documents & Attachments</h3>
      </div>
      <div style={styles.cardContent}>
        <p style={{ color: '#6b7280', textAlign: 'center', padding: '20px' }}>
          Document management will be implemented here. This will show proposals, contracts, and other files related to this lead.
        </p>
      </div>
    </div>
  );

  const renderTimelineTab = () => (
    <div style={styles.card}>
      <div style={styles.cardHeader}>
        <h3 style={styles.cardTitle}>Timeline & History</h3>
      </div>
      <div style={styles.cardContent}>
        <p style={{ color: '#6b7280', textAlign: 'center', padding: '20px' }}>
          Complete timeline and history of interactions with this lead will be displayed here.
        </p>
      </div>
    </div>
  );

  const renderTabContent = () => {
    switch (activeTab) {
      case 'overview':
        return renderOverviewTab();
      case 'deals':
        return renderDealsTab();
      case 'activity':
        return renderActivityTab();
      case 'tasks':
        return renderTasksTab();
      case 'attachments':
        return renderAttachmentsTab();
      case 'timeline':
        return renderTimelineTab();
      default:
        return renderOverviewTab();
    }
  };

  return (
    <div style={styles.container}>
      {/* Lead Information Card */}
      <div style={styles.leadCard}>
        <div style={styles.leadHeader}>
          <div style={styles.leadLeft}>
            <div style={styles.topRow}>
              <div style={styles.companyLogo}>
                {lead.company.charAt(0).toUpperCase()}
              </div>
              <div style={styles.nameAndDetails}>
                <h1 style={styles.leadTitle}>{lead.name}</h1>
                <div style={styles.bottomInfo}>
                  <div style={styles.titleInfo}>
                    <User size={14} />
                    <span>CEO</span>
                  </div>
                  <div style={styles.companyInfo}>
                    <Building size={14} />
                    <span>{lead.company}</span>
                  </div>
                </div>
              </div>
            </div>
          </div>
          <div style={styles.leadRight}>
            <button 
              style={styles.convertButton}
              onMouseEnter={(e) => {
                e.currentTarget.style.backgroundColor = '#15803d';
              }}
              onMouseLeave={(e) => {
                e.currentTarget.style.backgroundColor = '#16a34a';
              }}
            >
              <UserPlus size={16} />
              Convert to Lead
            </button>
          </div>
        </div>
      </div>

      {/* Pipeline Card */}
      {renderPipelineView()}

      {/* Tabs Card */}
      <div style={styles.tabsCard}>
        <div style={styles.tabsContainer}>
          <div style={styles.tabs}>
            {tabs.map((tab) => {
              const IconComponent = tab.icon;
              return (
                <button
                  key={tab.id}
                  style={{
                    ...styles.tab,
                    ...(activeTab === tab.id ? styles.tabActive : {})
                  }}
                  onClick={() => setActiveTab(tab.id)}
                  onMouseEnter={(e) => {
                    if (activeTab !== tab.id) {
                      e.currentTarget.style.backgroundColor = '#f8fafc';
                    }
                  }}
                  onMouseLeave={(e) => {
                    if (activeTab !== tab.id) {
                      e.currentTarget.style.backgroundColor = 'transparent';
                    }
                  }}
                >
                  <IconComponent size={14} />
                  {tab.label}
                </button>
              );
            })}
          </div>
        </div>
        <div style={styles.tabContent}>
          {renderTabContent()}
        </div>
      </div>
    </div>
  );
};

export default LeadDetails;
