import React, { useState } from 'react';
import { 
  ArrowLeft, 
  Phone, 
  Mail, 
  Building, 
  User, 
  Edit2, 
  Save,
  CheckCircle,
  Circle,
  ChevronRight,
  FileText,
  MessageSquare,
  History
} from 'lucide-react';

interface Lead {
  id: number;
  name: string;
  company: string;
  email: string;
  phone: string;
  status: string;
  value: number;
  source: string;
  created: string;
}

interface LeadDetailsProps {
  lead: Lead;
  onBack: () => void;
  onSave: (updatedLead: Lead) => void;
}

const LeadDetails: React.FC<LeadDetailsProps> = ({ lead, onBack, onSave }) => {
  const [activeTab, setActiveTab] = useState('overview');
  const [editingField, setEditingField] = useState<string | null>(null);
  const [editedLead, setEditedLead] = useState(lead);
  const [currentStage, setCurrentStage] = useState(lead.status);

  const pipelineStages = [
    { id: 'New', label: 'New', color: '#6b7280' },
    { id: 'Contacted', label: 'Contacted', color: '#3b82f6' },
    { id: 'Qualified', label: 'Qualified', color: '#8b5cf6' },
    { id: 'Proposal', label: 'Proposal', color: '#f59e0b' },
    { id: 'Closed', label: 'Closed', color: '#10b981' }
  ];

  const tabs = [
    { id: 'overview', label: 'Overview', icon: User },
    { id: 'activity', label: 'Activity', icon: MessageSquare },
    { id: 'documents', label: 'Documents', icon: FileText },
    { id: 'history', label: 'History', icon: History }
  ];

  const handleFieldEdit = (field: string) => {
    setEditingField(field);
  };

  const handleFieldSave = () => {
    setEditingField(null);
    onSave(editedLead);
  };

  const handleFieldChange = (field: string, value: string | number) => {
    setEditedLead(prev => ({ ...prev, [field]: value }));
  };

  const handleStageChange = (stage: string) => {
    setCurrentStage(stage);
    handleFieldChange('status', stage);
  };

  const getCurrentStageIndex = () => {
    return pipelineStages.findIndex(stage => stage.id === currentStage);
  };

  const styles = {
    container: {
      display: 'flex',
      flexDirection: 'column' as const,
      height: '100vh',
      backgroundColor: '#f8fafc',
      fontFamily: '"Inter", sans-serif'
    },
    header: {
      backgroundColor: 'white',
      borderBottom: '1px solid #e2e8f0',
      padding: '24px 32px',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'space-between'
    },
    headerLeft: {
      display: 'flex',
      alignItems: 'center',
      gap: '16px'
    },
    backButton: {
      display: 'flex',
      alignItems: 'center',
      gap: '8px',
      padding: '8px 16px',
      backgroundColor: '#f1f5f9',
      border: 'none',
      borderRadius: '8px',
      color: '#475569',
      cursor: 'pointer',
      fontSize: '14px',
      fontWeight: '500',
      transition: 'all 0.2s'
    },
    leadTitle: {
      fontSize: '28px',
      fontWeight: '700',
      color: '#1e293b',
      margin: 0
    },
    leadSubtitle: {
      fontSize: '16px',
      color: '#64748b',
      margin: '4px 0 0 0'
    },
    headerRight: {
      display: 'flex',
      alignItems: 'center',
      gap: '12px'
    },
    quickInfo: {
      display: 'flex',
      flexDirection: 'column' as const,
      gap: '4px',
      textAlign: 'right' as const
    },
    quickInfoItem: {
      display: 'flex',
      alignItems: 'center',
      gap: '8px',
      fontSize: '14px',
      color: '#475569'
    },
    content: {
      flex: 1,
      display: 'flex',
      flexDirection: 'column' as const,
      overflow: 'hidden'
    },
    tabsContainer: {
      backgroundColor: 'white',
      borderBottom: '1px solid #e2e8f0',
      padding: '0 32px'
    },
    tabs: {
      display: 'flex',
      gap: '0'
    },
    tab: {
      display: 'flex',
      alignItems: 'center',
      gap: '8px',
      padding: '16px 24px',
      border: 'none',
      backgroundColor: 'transparent',
      color: '#64748b',
      cursor: 'pointer',
      fontSize: '14px',
      fontWeight: '500',
      borderBottom: '2px solid transparent',
      transition: 'all 0.2s'
    },
    tabActive: {
      color: '#3b82f6',
      borderBottomColor: '#3b82f6'
    },
    mainContent: {
      flex: 1,
      padding: '32px',
      overflow: 'auto'
    },
    grid: {
      display: 'grid',
      gridTemplateColumns: '2fr 1fr',
      gap: '32px',
      height: '100%'
    },
    card: {
      backgroundColor: 'white',
      borderRadius: '12px',
      border: '1px solid #e2e8f0',
      overflow: 'hidden'
    },
    cardHeader: {
      padding: '24px',
      borderBottom: '1px solid #e2e8f0',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'space-between'
    },
    cardTitle: {
      fontSize: '18px',
      fontWeight: '600',
      color: '#1e293b',
      margin: 0
    },
    cardContent: {
      padding: '24px'
    },
    fieldGroup: {
      marginBottom: '24px'
    },
    fieldLabel: {
      display: 'block',
      fontSize: '14px',
      fontWeight: '500',
      color: '#374151',
      marginBottom: '8px'
    },
    fieldValue: {
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'space-between',
      padding: '12px 16px',
      backgroundColor: '#f8fafc',
      border: '1px solid #e2e8f0',
      borderRadius: '8px',
      fontSize: '14px',
      color: '#1e293b'
    },
    fieldInput: {
      width: '100%',
      padding: '12px 16px',
      border: '1px solid #d1d5db',
      borderRadius: '8px',
      fontSize: '14px',
      outline: 'none',
      transition: 'border-color 0.2s'
    },
    editButton: {
      padding: '4px',
      backgroundColor: 'transparent',
      border: 'none',
      color: '#6b7280',
      cursor: 'pointer',
      borderRadius: '4px',
      transition: 'color 0.2s'
    },
    saveButton: {
      display: 'flex',
      alignItems: 'center',
      gap: '8px',
      padding: '8px 16px',
      backgroundColor: '#3b82f6',
      color: 'white',
      border: 'none',
      borderRadius: '8px',
      cursor: 'pointer',
      fontSize: '14px',
      fontWeight: '500'
    },
    pipeline: {
      padding: '24px 0'
    },
    pipelineTitle: {
      fontSize: '16px',
      fontWeight: '600',
      color: '#1e293b',
      marginBottom: '24px',
      textAlign: 'center' as const
    },
    stagesContainer: {
      display: 'flex',
      flexDirection: 'column' as const,
      gap: '16px'
    },
    stage: {
      display: 'flex',
      alignItems: 'center',
      gap: '12px',
      padding: '16px',
      borderRadius: '8px',
      cursor: 'pointer',
      transition: 'all 0.2s',
      border: '1px solid #e2e8f0'
    },
    stageActive: {
      backgroundColor: '#eff6ff',
      borderColor: '#3b82f6'
    },
    stageCompleted: {
      backgroundColor: '#f0fdf4',
      borderColor: '#10b981'
    },
    stageCircle: {
      width: '20px',
      height: '20px',
      borderRadius: '50%',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center'
    },
    stageLabel: {
      flex: 1,
      fontSize: '14px',
      fontWeight: '500'
    },
    closedOptions: {
      marginTop: '16px',
      padding: '16px',
      backgroundColor: '#f8fafc',
      borderRadius: '8px',
      border: '1px solid #e2e8f0'
    },
    closedTitle: {
      fontSize: '14px',
      fontWeight: '500',
      color: '#374151',
      marginBottom: '12px'
    },
    closedButtons: {
      display: 'flex',
      gap: '8px'
    },
    closedButton: {
      flex: 1,
      padding: '8px 16px',
      border: '1px solid #d1d5db',
      borderRadius: '6px',
      backgroundColor: 'white',
      cursor: 'pointer',
      fontSize: '14px',
      fontWeight: '500',
      transition: 'all 0.2s'
    },
    closedButtonWon: {
      backgroundColor: '#10b981',
      color: 'white',
      borderColor: '#10b981'
    },
    closedButtonLost: {
      backgroundColor: '#ef4444',
      color: 'white',
      borderColor: '#ef4444'
    }
  };

  const renderField = (label: string, field: keyof Lead, type: 'text' | 'email' | 'tel' | 'number' = 'text') => {
    const isEditing = editingField === field;
    const value = editedLead[field];

    return (
      <div style={styles.fieldGroup}>
        <label style={styles.fieldLabel}>{label}</label>
        {isEditing ? (
          <div style={{ display: 'flex', gap: '8px' }}>
            <input
              type={type}
              value={value}
              onChange={(e) => handleFieldChange(field, type === 'number' ? Number(e.target.value) : e.target.value)}
              style={styles.fieldInput}
            />
            <button style={styles.saveButton} onClick={handleFieldSave}>
              <Save size={16} />
            </button>
          </div>
        ) : (
          <div style={styles.fieldValue}>
            <span>{type === 'number' && field === 'value' ? `$${Number(value).toLocaleString()}` : value}</span>
            <button style={styles.editButton} onClick={() => handleFieldEdit(field)}>
              <Edit2 size={16} />
            </button>
          </div>
        )}
      </div>
    );
  };

  const renderOverviewTab = () => (
    <div style={styles.grid}>
      <div style={styles.card}>
        <div style={styles.cardHeader}>
          <h3 style={styles.cardTitle}>Lead Information</h3>
        </div>
        <div style={styles.cardContent}>
          {renderField('Full Name', 'name')}
          {renderField('Company', 'company')}
          {renderField('Email', 'email', 'email')}
          {renderField('Phone', 'phone', 'tel')}
          {renderField('Source', 'source')}
          {renderField('Estimated Value', 'value', 'number')}
        </div>
      </div>

      <div style={styles.card}>
        <div style={styles.cardHeader}>
          <h3 style={styles.cardTitle}>Sales Pipeline</h3>
        </div>
        <div style={styles.pipeline}>
          <div style={styles.stagesContainer}>
            {pipelineStages.map((stage, index) => {
              const currentIndex = getCurrentStageIndex();
              const isCompleted = index < currentIndex;
              const isCurrent = index === currentIndex;

              return (
                <div
                  key={stage.id}
                  style={{
                    ...styles.stage,
                    ...(isCurrent ? styles.stageActive : {}),
                    ...(isCompleted ? styles.stageCompleted : {})
                  }}
                  onClick={() => handleStageChange(stage.id)}
                >
                  <div style={styles.stageCircle}>
                    {isCompleted ? (
                      <CheckCircle size={20} color="#10b981" />
                    ) : isCurrent ? (
                      <Circle size={20} color="#3b82f6" fill="#3b82f6" />
                    ) : (
                      <Circle size={20} color="#9ca3af" />
                    )}
                  </div>
                  <span style={{
                    ...styles.stageLabel,
                    color: isCurrent ? '#3b82f6' : isCompleted ? '#10b981' : '#6b7280'
                  }}>
                    {stage.label}
                  </span>
                  {index < pipelineStages.length - 1 && (
                    <ChevronRight size={16} color="#d1d5db" />
                  )}
                </div>
              );
            })}
          </div>

          {currentStage === 'Closed' && (
            <div style={styles.closedOptions}>
              <div style={styles.closedTitle}>Outcome</div>
              <div style={styles.closedButtons}>
                <button
                  style={{
                    ...styles.closedButton,
                    ...styles.closedButtonWon
                  }}
                  onClick={() => handleFieldChange('status', 'Closed Won')}
                >
                  Closed Won
                </button>
                <button
                  style={{
                    ...styles.closedButton,
                    ...styles.closedButtonLost
                  }}
                  onClick={() => handleFieldChange('status', 'Closed Lost')}
                >
                  Closed Lost
                </button>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );

  const renderActivityTab = () => (
    <div style={styles.card}>
      <div style={styles.cardHeader}>
        <h3 style={styles.cardTitle}>Activity Timeline</h3>
      </div>
      <div style={styles.cardContent}>
        <p style={{ color: '#6b7280', textAlign: 'center', padding: '40px' }}>
          Activity tracking will be implemented here. This will show emails, calls, meetings, and other interactions with this lead.
        </p>
      </div>
    </div>
  );

  const renderDocumentsTab = () => (
    <div style={styles.card}>
      <div style={styles.cardHeader}>
        <h3 style={styles.cardTitle}>Documents & Files</h3>
      </div>
      <div style={styles.cardContent}>
        <p style={{ color: '#6b7280', textAlign: 'center', padding: '40px' }}>
          Document management will be implemented here. This will show proposals, contracts, and other files related to this lead.
        </p>
      </div>
    </div>
  );

  const renderHistoryTab = () => (
    <div style={styles.card}>
      <div style={styles.cardHeader}>
        <h3 style={styles.cardTitle}>Change History</h3>
      </div>
      <div style={styles.cardContent}>
        <p style={{ color: '#6b7280', textAlign: 'center', padding: '40px' }}>
          Change history will be implemented here. This will show all modifications made to this lead over time.
        </p>
      </div>
    </div>
  );

  const renderTabContent = () => {
    switch (activeTab) {
      case 'overview':
        return renderOverviewTab();
      case 'activity':
        return renderActivityTab();
      case 'documents':
        return renderDocumentsTab();
      case 'history':
        return renderHistoryTab();
      default:
        return renderOverviewTab();
    }
  };

  return (
    <div style={styles.container}>
      {/* Header */}
      <div style={styles.header}>
        <div style={styles.headerLeft}>
          <button style={styles.backButton} onClick={onBack}>
            <ArrowLeft size={16} />
            Back to Leads
          </button>
          <div>
            <h1 style={styles.leadTitle}>{lead.name}</h1>
            <p style={styles.leadSubtitle}>{lead.company}</p>
          </div>
        </div>
        <div style={styles.headerRight}>
          <div style={styles.quickInfo}>
            <div style={styles.quickInfoItem}>
              <Phone size={16} />
              <span>{lead.phone}</span>
            </div>
            <div style={styles.quickInfoItem}>
              <Mail size={16} />
              <span>{lead.email}</span>
            </div>
            <div style={styles.quickInfoItem}>
              <Building size={16} />
              <span>{lead.company}</span>
            </div>
          </div>
        </div>
      </div>

      {/* Tabs */}
      <div style={styles.tabsContainer}>
        <div style={styles.tabs}>
          {tabs.map((tab) => {
            const IconComponent = tab.icon;
            return (
              <button
                key={tab.id}
                style={{
                  ...styles.tab,
                  ...(activeTab === tab.id ? styles.tabActive : {})
                }}
                onClick={() => setActiveTab(tab.id)}
              >
                <IconComponent size={16} />
                {tab.label}
              </button>
            );
          })}
        </div>
      </div>

      {/* Main Content */}
      <div style={styles.mainContent}>
        {renderTabContent()}
      </div>
    </div>
  );
};

export default LeadDetails;
