sourcesContent:
import React, { useState } from 'react';
import { 
  ArrowLeft, 
  Phone, 
  Mail, 
  Building, 
  User, 
  CheckCircle,
  ChevronRight,
  MessageSquare,
  Briefcase,
  Paperclip,
  Clock,
  UserPlus,
  Plus,
  Calendar,
  ArrowUpDown,
  X,
  Upload,
  Edit2,
  Check
} from 'lucide-react';

interface Lead {
  id: number;
  name: string;
  company: string;
  email: string;
  phone: string;
  status: string;
  value: number;
  source: string;
  created: string;
}

interface LeadDetailsProps {
  lead: Lead;
  onBack: () => void;
  onSave: (updatedLead: Lead) => void;
}

const LeadDetails: React.FC<LeadDetailsProps> = ({ lead, onBack, onSave }) => {
  const [activeTab, setActiveTab] = useState('overview');
  const [currentStage, setCurrentStage] = useState(lead.status);

  // Task management state
  const [tasks, setTasks] = useState([
    {
      id: 1,
      title: 'Follow up call',
      description: 'Call John Smith to discuss proposal details and next steps',
      deadline: '2024-01-20',
      priority: 'high',
      status: 'pending',
      createdDate: '2024-01-15'
    },
    {
      id: 2,
      title: 'Send contract draft',
      description: 'Prepare and send the initial contract draft for review',
      deadline: '2024-01-25',
      priority: 'medium',
      status: 'pending',
      createdDate: '2024-01-14'
    },
    {
      id: 3,
      title: 'Schedule demo meeting',
      description: 'Coordinate with the team to schedule a product demo',
      deadline: '2024-01-18',
      priority: 'high',
      status: 'completed',
      createdDate: '2024-01-12'
    },
    {
      id: 4,
      title: 'Gather requirements',
      description: 'Collect detailed requirements from the client',
      deadline: '2024-01-30',
      priority: 'low',
      status: 'pending',
      createdDate: '2024-01-10'
    }
  ]);

  const [showTaskForm, setShowTaskForm] = useState(false);
  const [taskForm, setTaskForm] = useState({
    title: '',
    description: '',
    deadline: '',
    priority: 'medium'
  });

  const [taskSortBy, setTaskSortBy] = useState('deadline');
  const [taskSortDirection, setTaskSortDirection] = useState('asc');

  // Attachment management state
  const [showAttachmentModal, setShowAttachmentModal] = useState(false);
  const [attachments, setAttachments] = useState([
    {
      id: 1,
      name: 'Proposal_TechCorp_2024.pdf',
      size: '2.4 MB',
      uploadedBy: 'John Smith',
      uploadedDate: '2024-01-15',
      type: 'PDF'
    },
    {
      id: 2,
      name: 'Contract_Draft_v2.docx',
      size: '1.1 MB',
      uploadedBy: 'Sarah Johnson',
      uploadedDate: '2024-01-12',
      type: 'DOCX'
    },
    {
      id: 3,
      name: 'Company_Presentation.pptx',
      size: '5.7 MB',
      uploadedBy: 'Mike Wilson',
      uploadedDate: '2024-01-10',
      type: 'PPTX'
    },
    {
      id: 4,
      name: 'Meeting_Notes_Jan8.txt',
      size: '45 KB',
      uploadedBy: 'John Smith',
      uploadedDate: '2024-01-08',
      type: 'TXT'
    }
  ]);

  // Field editing state
  const [editingField, setEditingField] = useState<string | null>(null);
  const [hoveredField, setHoveredField] = useState<string | null>(null);
  const [fieldValues, setFieldValues] = useState({
    'First Name': 'John',
    'Last Name': 'Smith',
    'Email': 'john.smith@techcorp.com',
    'Phone': '+1-555-0123',
    'Title': 'CEO',
    'Company Name': 'Tech Corp',
    'Industry': 'Technology',
    'Website': 'www.techcorp.com',
    'No. of Employees': '50-100',
    'Address Line 1 (Street Address)': '123 Business Avenue',
    'Address Line 2 (Area, Street, Sector, Village)': 'Downtown Business District',
    'Landmark (optional)': 'Near Central Park',
    'City': 'New York',
    'State': 'NY',
    'Country': 'United States',
    'Postal Index': '10001',
    'Description': 'High-potential lead from technology sector. Interested in enterprise solutions and has budget approval authority. Previous interactions have been positive and they\'ve expressed strong interest in our services.'
  });

  const pipelineStages = [
    { id: 'New', label: 'New', number: 1 },
    { id: 'Contacted', label: 'Contacted', number: 2 },
    { id: 'Qualified', label: 'Qualified', number: 3 },
    { id: 'Proposal', label: 'Proposal', number: 4 }
  ];

  const tabs = [
    { id: 'overview', label: 'Overview', icon: User },
    { id: 'deals', label: 'Deals', icon: Briefcase },
    { id: 'activity', label: 'Activity', icon: MessageSquare },
    { id: 'tasks', label: 'Tasks', icon: CheckCircle },
    { id: 'attachments', label: 'Attachments', icon: Paperclip },
    { id: 'timeline', label: 'Timeline', icon: Clock }
  ];

  const handleStageChange = (stage: string) => {
    setCurrentStage(stage);
  };

  const getCurrentStageIndex = () => {
    return pipelineStages.findIndex(stage => stage.id === currentStage);
  };

  const getStageStatus = (stage: any, index: number) => {
    const currentIndex = getCurrentStageIndex();
    return {
      completed: index < currentIndex,
      active: index === currentIndex,
      inactive: index > currentIndex
    };
  };

  // Task handler functions
  const handleTaskFormChange = (field: string, value: string) => {
    setTaskForm(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const handleTaskSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (!taskForm.title.trim()) return;

    const newTask = {
      id: Date.now(),
      title: taskForm.title,
      description: taskForm.description,
      deadline: taskForm.deadline,
      priority: taskForm.priority,
      status: 'pending',
      createdDate: new Date().toISOString().split('T')[0]
    };

    setTasks(prev => [...prev, newTask]);
    setTaskForm({ title: '', description: '', deadline: '', priority: 'medium' });
    setShowTaskForm(false);
  };

  const handleTaskSort = (field: string) => {
    if (taskSortBy === field) {
      setTaskSortDirection(taskSortDirection === 'asc' ? 'desc' : 'asc');
    } else {
      setTaskSortBy(field);
      setTaskSortDirection('asc');
    }
  };

  const getSortedTasks = () => {
    return [...tasks].sort((a, b) => {
      let aValue: any = a[taskSortBy as keyof typeof a];
      let bValue: any = b[taskSortBy as keyof typeof b];

      if (taskSortBy === 'priority') {
        const priorityOrder = { high: 3, medium: 2, low: 1 };
        aValue = priorityOrder[aValue as keyof typeof priorityOrder];
        bValue = priorityOrder[bValue as keyof typeof priorityOrder];
      }

      if (taskSortBy === 'deadline') {
        aValue = new Date(aValue).getTime();
        bValue = new Date(bValue).getTime();
      }

      if (aValue < bValue) return taskSortDirection === 'asc' ? -1 : 1;
      if (aValue > bValue) return taskSortDirection === 'asc' ? 1 : -1;
      return 0;
    });
  };

  const getPriorityStyle = (priority: string) => {
    switch (priority) {
      case 'high': return styles.priorityHigh;
      case 'medium': return styles.priorityMedium;
      case 'low': return styles.priorityLow;
      default: return styles.priorityMedium;
    }
  };

  const getStatusStyle = (status: string) => {
    switch (status) {
      case 'completed': return { ...styles.taskStatus, ...styles.statusCompleted };
      case 'pending': return { ...styles.taskStatus, ...styles.statusPending };
      default: return { ...styles.taskStatus, ...styles.statusPending };
    }
  };

  // Attachment handler functions
  const handleFileUpload = (event: React.ChangeEvent<HTMLInputElement>) => {
    const files = event.target.files;
    if (!files) return;

    Array.from(files).forEach(file => {
      const newAttachment = {
        id: Date.now() + Math.random(),
        name: file.name,
        size: formatFileSize(file.size),
        uploadedBy: 'John Smith', // Current user
        uploadedDate: new Date().toISOString().split('T')[0],
        type: getFileType(file.name)
      };

      setAttachments(prev => [...prev, newAttachment]);
    });

    setShowAttachmentModal(false);
    // Reset file input
    event.target.value = '';
  };

  const formatFileSize = (bytes: number): string => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(1)) + ' ' + sizes[i];
  };

  const getFileType = (filename: string): string => {
    const extension = filename.split('.').pop()?.toUpperCase();
    return extension || 'FILE';
  };

  const triggerFileUpload = () => {
    const fileInput = document.getElementById('fileInput') as HTMLInputElement;
    fileInput?.click();
  };

  // Field editing handler functions
  const handleFieldEdit = (fieldName: string) => {
    setEditingField(fieldName);
  };

  const handleFieldSave = (fieldName: string, newValue: string) => {
    setFieldValues(prev => ({
      ...prev,
      [fieldName]: newValue
    }));
    setEditingField(null);
  };

  const handleFieldCancel = () => {
    setEditingField(null);
  };

  const handleFieldHover = (fieldName: string | null) => {
    if (editingField === null) {
      setHoveredField(fieldName);
    }
  };

  const renderTaskModal = () => {
    if (!showTaskForm) return null;

    return (
      <div style={styles.modalOverlay} onClick={() => setShowTaskForm(false)}>
        <div style={styles.modal} onClick={(e) => e.stopPropagation()}>
          <div style={styles.modalHeader}>
            <h3 style={styles.modalTitle}>Create New Task</h3>
            <button 
              style={styles.modalCloseButton}
              onClick={() => setShowTaskForm(false)}
              onMouseEnter={(e) => {
                (e.target as HTMLElement).style.backgroundColor = '#f3f4f6';
              }}
              onMouseLeave={(e) => {
                (e.target as HTMLElement).style.backgroundColor = 'transparent';
              }}
            >
              <X size={18} />
            </button>
          </div>

          <form onSubmit={handleTaskSubmit}>
            <div style={styles.modalBody}>
              <div style={styles.modalFormField}>
                <label style={styles.modalLabel}>Title *</label>
                <input
                  type="text"
                  style={styles.modalInput}
                  value={taskForm.title}
                  onChange={(e) => handleTaskFormChange('title', e.target.value)}
                  placeholder="Enter task title"
                  required
                />
              </div>

              <div style={styles.modalFormField}>
                <label style={styles.modalLabel}>Description</label>
                <textarea
                  style={styles.modalTextarea}
                  value={taskForm.description}
                  onChange={(e) => handleTaskFormChange('description', e.target.value)}
                  placeholder="Enter task description"
                />
              </div>

              <div style={styles.modalFormRow}>
                <div style={styles.modalFormFieldHalf}>
                  <label style={styles.modalLabel}>Deadline</label>
                  <input
                    type="date"
                    style={styles.modalInput}
                    value={taskForm.deadline}
                    onChange={(e) => handleTaskFormChange('deadline', e.target.value)}
                  />
                </div>
                <div style={styles.modalFormFieldHalf}>
                  <label style={styles.modalLabel}>Priority</label>
                  <select
                    style={styles.modalSelect}
                    value={taskForm.priority}
                    onChange={(e) => handleTaskFormChange('priority', e.target.value)}
                  >
                    <option value="low">Low</option>
                    <option value="medium">Medium</option>
                    <option value="high">High</option>
                  </select>
                </div>
              </div>
            </div>

            <div style={styles.modalFooter}>
              <button 
                type="button" 
                style={styles.modalCancelButton}
                onClick={() => setShowTaskForm(false)}
                onMouseEnter={(e) => {
                  (e.target as HTMLElement).style.backgroundColor = '#4b5563';
                }}
                onMouseLeave={(e) => {
                  (e.target as HTMLElement).style.backgroundColor = '#6b7280';
                }}
              >
                Cancel
              </button>
              <button 
                type="submit" 
                style={styles.modalSubmitButton}
                onMouseEnter={(e) => {
                  (e.target as HTMLElement).style.backgroundColor = '#047857';
                }}
                onMouseLeave={(e) => {
                  (e.target as HTMLElement).style.backgroundColor = '#059669';
                }}
              >
                <CheckCircle size={14} />
                Create Task
              </button>
            </div>
          </form>
        </div>
      </div>
    );
  };

  const renderAttachmentModal = () => {
    if (!showAttachmentModal) return null;

    return (
      <div style={styles.modalOverlay} onClick={() => setShowAttachmentModal(false)}>
        <div style={styles.uploadModal} onClick={(e) => e.stopPropagation()}>
          <div style={styles.modalHeader}>
            <h3 style={styles.modalTitle}>Upload Files</h3>
            <button 
              style={styles.modalCloseButton}
              onClick={() => setShowAttachmentModal(false)}
              onMouseEnter={(e) => {
                (e.target as HTMLElement).style.backgroundColor = '#f3f4f6';
              }}
              onMouseLeave={(e) => {
                (e.target as HTMLElement).style.backgroundColor = 'transparent';
              }}
            >
              <X size={18} />
            </button>
          </div>

          <div 
            style={styles.uploadArea}
            onClick={triggerFileUpload}
            onMouseEnter={(e) => {
              const target = e.target as HTMLElement;
              target.style.borderColor = '#2563eb';
              target.style.backgroundColor = '#eff6ff';
            }}
            onMouseLeave={(e) => {
              const target = e.target as HTMLElement;
              target.style.borderColor = '#d1d5db';
              target.style.backgroundColor = '#f9fafb';
            }}
          >
            <div style={styles.uploadIcon}>
              <Upload size={40} />
            </div>
            <div style={styles.uploadText}>Click to upload files</div>
            <div style={styles.uploadSubtext}>
              Select files from your computer to upload
            </div>
          </div>

          <input
            id="fileInput"
            type="file"
            multiple
            style={styles.hiddenFileInput}
            onChange={handleFileUpload}
            accept="*/*"
          />

          <div style={styles.modalFooter}>
            <button 
              type="button" 
              style={styles.modalCancelButton}
              onClick={() => setShowAttachmentModal(false)}
              onMouseEnter={(e) => {
                (e.target as HTMLElement).style.backgroundColor = '#4b5563';
              }}
              onMouseLeave={(e) => {
                (e.target as HTMLElement).style.backgroundColor = '#6b7280';
              }}
            >
              Cancel
            </button>
          </div>
        </div>
      </div>
    );
  };

  const styles = {
    container: {
      display: 'flex',
      flexDirection: 'column' as const,
      height: '100vh',
      backgroundColor: '#f8fafc',
      fontFamily: '"Inter", sans-serif'
    },
    leadCard: {
      backgroundColor: 'white',
      borderRadius: '8px',
      border: '1px solid #e2e8f0',
      margin: '4px 8px',
      padding: '8px',
      boxShadow: '0 1px 2px rgba(0, 0, 0, 0.1)',
      flexShrink: 0
    },
    leadHeader: {
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'space-between'
    },
    leadLeft: {
      display: 'flex',
      flexDirection: 'row' as const,
      gap: '2px',
      alignItems: 'flex-start'
    },
    logoSection: {
      display: 'flex',
      flexDirection: 'column' as const,
      gap: '2px'
    },
    nameAndDetails: {
      display: 'flex',
      flexDirection: 'column' as const,
      gap: '1px'
    },
    companyBelowLogo: {
      display: 'flex',
      alignItems: 'center',
      gap: '4px',
      fontSize: '10px',
      color: '#9ca3af',
      margin: 0,
      marginTop: '2px',
      fontStyle: 'italic',
      fontWeight: '300'
    },
    companyLogo: {
      width: '40px',
      height: '40px',
      borderRadius: '0px',
      backgroundColor: '#3b82f6',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      color: 'white',
      fontSize: '18px',
      fontWeight: '900'
    },
    leadTitle: {
      fontSize: '36px',
      fontWeight: '900',
      color: '#1e293b',
      margin: 0,
      lineHeight: '1.0'
    },
    titleInfo: {
      fontSize: '11px',
      color: '#64748b',
      margin: 0,
      marginTop: '-2px',
      textAlign: 'left' as const
    },
    leadRight: {
      display: 'flex',
      alignItems: 'center',
      gap: '10px'
    },
    convertButton: {
      display: 'flex',
      alignItems: 'center',
      gap: '8px',
      padding: '8px 16px',
      backgroundColor: '#16a34a',
      color: 'white',
      border: 'none',
      borderRadius: '6px',
      cursor: 'pointer',
      fontSize: '14px',
      fontWeight: '500',
      transition: 'background-color 0.2s'
    },
    content: {
      flex: 1,
      display: 'flex',
      flexDirection: 'column' as const,
      overflow: 'hidden',
      minHeight: 0
    },
    tabsCard: {
      backgroundColor: 'white',
      borderRadius: '8px',
      border: '1px solid #e2e8f0',
      margin: '0 8px 8px 8px',
      boxShadow: '0 1px 2px rgba(0, 0, 0, 0.1)',
      overflow: 'hidden',
      flex: 1,
      display: 'flex',
      flexDirection: 'column' as const,
      minHeight: 0
    },
    tabsContainer: {
      padding: '0',
      flexShrink: 0
    },
    tabs: {
      display: 'flex',
      gap: '0',
      borderBottom: '1px solid #e2e8f0'
    },
    tab: {
      display: 'flex',
      alignItems: 'center',
      gap: '6px',
      padding: '6px 12px',
      border: 'none',
      backgroundColor: 'transparent',
      color: '#64748b',
      cursor: 'pointer',
      fontSize: '13px',
      fontWeight: '500',
      borderBottom: '2px solid transparent',
      transition: 'color 0.2s, background-color 0.2s',
      position: 'relative' as const
    },
    tabActive: {
      color: '#3b82f6',
      borderBottom: '2px solid #3b82f6',
      backgroundColor: '#f8fafc'
    },
    tabContent: {
      padding: '8px',
      flex: 1,
      overflow: 'auto',
      minHeight: 0
    },
    mainContent: {
      flex: 1,
      padding: '0 8px 8px 8px',
      overflow: 'auto'
    },
    grid: {
      display: 'grid',
      gridTemplateColumns: '2fr 0.6fr',
      gap: '8px',
      height: '100%',
      minHeight: 0
    },
    scrollableColumn: {
      display: 'flex',
      flexDirection: 'column' as const,
      gap: '6px',
      overflow: 'auto',
      minHeight: 0,
      paddingRight: '3px'
    },
    card: {
      backgroundColor: 'white',
      borderRadius: '0',
      border: '1px solid #e2e8f0',
      overflow: 'hidden',
      boxShadow: '0 1px 2px rgba(0, 0, 0, 0.1)',
      marginBottom: '6px',
      flexShrink: 0
    },
    cardHeader: {
      padding: '8px 10px',
      borderBottom: '1px solid #e2e8f0',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'space-between'
    },
    cardTitle: {
      fontSize: '13px',
      fontWeight: '600',
      color: '#1e293b',
      margin: 0
    },
    cardContent: {
      padding: '8px 10px'
    },
    fieldGroup: {
      marginBottom: '8px'
    },
    fieldGroupLast: {
      marginBottom: '0'
    },
    fieldLabel: {
      display: 'block',
      fontSize: '11px',
      fontWeight: '500',
      color: '#374151',
      marginBottom: '4px'
    },
    fieldValueDisplay: {
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'space-between',
      padding: '6px 8px',
      backgroundColor: '#f8fafc',
      border: '1px solid #e2e8f0',
      borderRadius: '4px',
      fontSize: '13px',
      color: '#1e293b'
    },
    // Field editing styles
    editableFieldContainer: {
      position: 'relative' as const
    },
    fieldEditButton: {
      display: 'flex',
      alignItems: 'center',
      gap: '4px',
      padding: '2px 6px',
      backgroundColor: '#2563eb',
      color: 'white',
      border: 'none',
      borderRadius: '3px',
      cursor: 'pointer',
      fontSize: '10px',
      fontWeight: '500',
      opacity: 0,
      transition: 'opacity 0.2s',
      position: 'absolute' as const,
      right: '4px',
      top: '50%',
      transform: 'translateY(-50%)',
      zIndex: 10
    },
    fieldEditButtonVisible: {
      opacity: 1
    },
    fieldEditInput: {
      width: '100%',
      padding: '6px 60px 6px 8px',
      border: '1px solid #2563eb',
      borderRadius: '4px',
      fontSize: '13px',
      color: '#1e293b',
      backgroundColor: 'white',
      outline: 'none'
    },
    fieldEditTextarea: {
      width: '100%',
      padding: '6px 60px 6px 8px',
      border: '1px solid #2563eb',
      borderRadius: '4px',
      fontSize: '13px',
      color: '#1e293b',
      backgroundColor: 'white',
      outline: 'none',
      minHeight: '60px',
      resize: 'vertical' as const
    },
