  const renderDisplayField = (label: string, value: string, isLast: boolean = false) => {
    return (
      <div style={isLast ? styles.fieldGroupLast : styles.fieldGroup}>
        <label style={styles.fieldLabel}>{label}</label>
        <div 
          style={styles.fieldValueDisplay}
        >
          <span>{value}</span>
        </div>
      </div>
    );
  };

  const renderField = (label: string, field: keyof Lead, type: 'text' | 'email' | 'tel' | 'number' = 'text') => {
    const value = lead[field];

    return (
      <div style={styles.fieldGroup}>
        <label style={styles.fieldLabel}>{label}</label>
        <div 
          style={styles.fieldValueDisplay}
        >
          <span>{type === 'number' && field === 'value' ? `$${Number(value).toLocaleString()}` : value}</span>
        </div>
      </div>
    );
  };

  const renderPipelineView = () => {
    const currentIndex = getCurrentStageIndex();
    const isLastStage = currentIndex === pipelineStages.length - 1;

    return (
      <div style={styles.pipelineContainer}>
        <div style={styles.pipelineSteps}>
          <div style={styles.pipelineStepsContainer}>
            {pipelineStages.map((stage, index) => {
              const status = getStageStatus(stage, index);

              return (
                <React.Fragment key={stage.id}>
                  <div style={styles.stageContainer}>
                    <div
                      style={{
                        ...styles.stage,
                        ...(status.completed ? styles.stageCompleted : {}),
                        ...(status.active ? styles.stageActive : {}),
                        ...(status.inactive ? styles.stageInactive : {})
                      }}
                      onClick={() => handleStageChange(stage.id)}
                    >
                      {status.completed && <CheckCircle size={14} style={{ marginRight: '6px' }} />}
                      {stage.label}

                      {/* Arrow shape */}
                      {index < pipelineStages.length - 1 && (
                        <div
                          style={{
                            ...styles.stageArrow,
                            borderLeftColor: status.completed ? '#d1fae5' : 
                                           status.active ? '#dbeafe' : '#f3f4f6'
                          }}
                        />
                      )}
                    </div>
                  </div>

                  {index < pipelineStages.length - 1 && (
                    <div
                      style={{
                        ...styles.stageConnector,
                        ...(status.completed ? styles.stageConnectorCompleted : {}),
                        ...(status.active ? styles.stageConnectorActive : {}),
                        ...(status.inactive ? styles.stageConnectorInactive : {})
                      }}
                    >
                      <ChevronRight
                        size={14}
                        color={
                          pipelineStages[index + 1] && 
                          (getStageStatus(pipelineStages[index + 1], index + 1).completed || 
                           getStageStatus(pipelineStages[index + 1], index + 1).active) 
                            ? '#1d4ed8' : '#9ca3af'
                        }
                      />
                    </div>
                  )}
                </React.Fragment>
              );
            })}
          </div>

          {isLastStage && (
            <button
              style={styles.markCompleteButton}
              onClick={() => handleStageChange('Closed')}
            >
              <CheckCircle size={11} />
              Mark Complete
            </button>
          )}
        </div>
      </div>
    );
  };

