import React, { useState } from 'react';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
  PointElement,
  LineElement,
} from 'chart.js';
import { Bar, Pie, Line } from 'react-chartjs-2';
import {
  Menu,
  X,
  Search,
  Filter,
  ArrowUpDown,
  Plus,
  BarChart3,
  Users,
  Building,
  Phone,
  HeartHandshake,
  FileText,

  Home,
  TrendingUp,
  Clock,
  Target,

  Settings,
  Bell,
  User,
  ChevronDown,
  UserPlus,
  Building2,
  Square,
  ArrowLeft,
  ChevronLeft,
  ChevronRight,
  Activity,
  MessageCircle,
  CheckSquare,
  Calendar as CalendarIcon,
  Mail as MailIcon,
  FileText as FileIcon,
  MoreVertical,
  ArrowUp,
  ArrowDown,
} from 'lucide-react';
import LeadsTable from './modules/Leads/LeadsTable';
import LeadDetails from './modules/Leads/LeadDetails';
import AccountsTable from './modules/Accounts/AccountsTable';
import AccountDetails from './modules/Accounts/AccountDetails';
import ContactsTable from './modules/Contacts/ContactsTable';
import ContactDetails from './modules/Contacts/ContactDetails';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
  PointElement,
  LineElement
);

const App: React.FC = () => {
  const [sidebarCollapsed, setSidebarCollapsed] = useState(false);
  const [rightSidebarCollapsed, setRightSidebarCollapsed] = useState(false);
  const [activeSection, setActiveSection] = useState<'dashboard' | 'leads' | 'accounts' | 'contacts' | 'deals'>('dashboard');
  const [showLeadModal, setShowLeadModal] = useState(false);
  const [showQuickActions, setShowQuickActions] = useState(false);
  const [showCreateLeadPage, setShowCreateLeadPage] = useState(false);
  const [selectedLead, setSelectedLead] = useState<any>(null);
  const [showLeadDetails, setShowLeadDetails] = useState(false);
  const [selectedContact, setSelectedContact] = useState<any>(null);
  const [showContactDetails, setShowContactDetails] = useState(false);
  const [selectedContacts, setSelectedContacts] = useState<number[]>([]);
  const [showContactActionMenu, setShowContactActionMenu] = useState<number | null>(null);
  const [selectedAccount, setSelectedAccount] = useState<any>(null);
  const [showAccountDetails, setShowAccountDetails] = useState(false);
  const [selectedAccounts, setSelectedAccounts] = useState<number[]>([]);
  const [showAccountActionMenu, setShowAccountActionMenu] = useState<number | null>(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [sortField, setSortField] = useState('');
  const [sortDirection, setSortDirection] = useState('asc');
  const [filterStatus, setFilterStatus] = useState('all');
  const [selectedLeads, setSelectedLeads] = useState<number[]>([]);
  const [showActionMenu, setShowActionMenu] = useState<number | null>(null);
  const [showSortMenu, setShowSortMenu] = useState(false);
  const [viewMode, setViewMode] = useState<'list' | 'kanban'>('list');
  const [expandedWidgets, setExpandedWidgets] = useState<{ [key: string]: boolean }>({
    activities: true,
    notes: true,
    tasks: true,
    emails: false,
    meetings: false,
  });

  const toggleWidget = (widgetId: string) => {
    setExpandedWidgets(prev => ({
      ...prev,
      [widgetId]: !prev[widgetId]
    }));
  };

  const handleSelectLead = (leadId: number) => {
    setSelectedLeads(prev => 
      prev.includes(leadId) 
        ? prev.filter(id => id !== leadId)
        : [...prev, leadId]
    );
  };

  const handleSelectAllLeads = () => {
    if (selectedLeads.length === filteredLeads.length) {
      setSelectedLeads([]);
    } else {
      setSelectedLeads(filteredLeads.map(lead => lead.id));
    }
  };

  const handleActionMenuClick = (leadId: number) => {
    setShowActionMenu(showActionMenu === leadId ? null : leadId);
  };

  const handleActionClick = (action: string, lead: any) => {
    setShowActionMenu(null);
