  const renderDisplayField = (label: string, fieldKey: string, isLast: boolean = false) => {
    const value = fieldValues[fieldKey as keyof typeof fieldValues] || '';
    const isEditing = editingField === fieldKey;
    const isHovered = hoveredField === fieldKey;

    const handleSave = () => {
      handleFieldSave(fieldKey, editingValue);
    };

    const handleCancel = () => {
      setEditingValue(value);
      handleFieldCancel();
    };

    const handleKeyPress = (e: React.KeyboardEvent) => {
      if (e.key === 'Enter' && !e.shiftKey) {
        e.preventDefault();
        handleSave();
      }
      if (e.key === 'Escape') {
        handleCancel();
      }
    };

    const isDescription = fieldKey === 'Description';

    return (
      <div style={isLast ? styles.fieldGroupLast : styles.fieldGroup}>
        <label style={styles.fieldLabel}>{label}</label>
        {isEditing ? (
          <>
            <div 
              style={{ ...styles.fieldValueDisplay, ...styles.editableFieldContainer }}
            >
              {isDescription ? (
                <textarea
                  style={styles.fieldEditTextarea}
                  value={editingValue}
                  onChange={(e) => setEditingValue(e.target.value)}
                  onKeyDown={handleKeyPress}
                  autoFocus
                />
              ) : (
                <input
                  type="text"
                  style={styles.fieldEditInput}
                  value={editingValue}
                  onChange={(e) => setEditingValue(e.target.value)}
                  onKeyDown={handleKeyPress}
                  autoFocus
                />
              )}
            </div>
            <div style={styles.fieldEditActions}>
              <button 
                style={styles.fieldSaveButton}
                onClick={handleSave}
                onMouseEnter={(e) => {
                  (e.target as HTMLElement).style.backgroundColor = '#047857';
                }}
                onMouseLeave={(e) => {
                  (e.target as HTMLElement).style.backgroundColor = '#059669';
                }}
              >
                <Check size={10} />
                Save
              </button>
              <button 
                style={styles.fieldCancelButton}
                onClick={handleCancel}
                onMouseEnter={(e) => {
                  (e.target as HTMLElement).style.backgroundColor = '#4b5563';
                }}
                onMouseLeave={(e) => {
                  (e.target as HTMLElement).style.backgroundColor = '#6b7280';
                }}
              >
                <X size={10} />
                Cancel
              </button>
            </div>
          </>
        ) : (
          <div 
            style={{ 
              ...(isDescription ? styles.fieldValueDisplayDescription : styles.fieldValueDisplay), 
              ...styles.editableFieldContainer,
              ...styles.fieldValueClickable
            }}
            onMouseEnter={() => handleFieldHover(fieldKey)}
            onMouseLeave={() => handleFieldHover(null)}
            onClick={() => handleFieldEdit(fieldKey)}
          >
            <span style={{ 
              flex: 1, 
              whiteSpace: isDescription ? 'pre-wrap' : 'normal',
              wordBreak: 'break-word',
              lineHeight: isDescription ? '1.5' : 'normal'
            }}>{value}</span>
            <button 
              style={{
                ...styles.fieldEditButton,
                ...(isHovered ? styles.fieldEditButtonVisible : {})
              }}
              onClick={(e) => {
                e.stopPropagation();
                handleFieldEdit(fieldKey);
              }}
              onMouseEnter={(e) => {
                (e.target as HTMLElement).style.backgroundColor = '#4b5563';
              }}
              onMouseLeave={(e) => {
                (e.target as HTMLElement).style.backgroundColor = '#6b7280';
              }}
            >
              <Edit2 size={10} />
            </button>
          </div>
        )}
      </div>
    );
  };



  const renderPipelineView = () => {
    const currentIndex = getCurrentStageIndex();
    const isLastStage = currentIndex === pipelineStages.length - 1;

    return (
      <div style={styles.pipelineContainer}>
        <div style={styles.pipelineSteps}>
          <div style={styles.pipelineStepsContainer}>
            {pipelineStages.map((stage, index) => {
              const status = getStageStatus(stage, index);

              return (
                <React.Fragment key={stage.id}>
                  <div style={styles.stageContainer}>
                    <div
                      style={{
                        ...styles.stage,
                        ...(status.completed ? styles.stageCompleted : {}),
                        ...(status.active ? styles.stageActive : {}),
                        ...(status.inactive ? styles.stageInactive : {})
                      }}
                      onClick={() => handleStageChange(stage.id)}
                    >
                      {status.completed && <CheckCircle size={14} style={{ marginRight: '6px' }} />}
                      {stage.label}

                      {/* Arrow shape */}
                      {index < pipelineStages.length - 1 && (
                        <div
                          style={{
                            ...styles.stageArrow,
                            borderLeftColor: status.completed ? '#d1fae5' : 
                                           status.active ? '#dbeafe' : '#f3f4f6'
                          }}
                        />
                      )}
                    </div>
                  </div>

                  {index < pipelineStages.length - 1 && (
                    <div
                      style={{
                        ...styles.stageConnector,
                        ...(status.completed ? styles.stageConnectorCompleted : {}),
                        ...(status.active ? styles.stageConnectorActive : {}),
                        ...(status.inactive ? styles.stageConnectorInactive : {})
                      }}
                    >
                      <ChevronRight
                        size={14}
                        color={
                          pipelineStages[index + 1] && 
                          (getStageStatus(pipelineStages[index + 1], index + 1).completed || 
                           getStageStatus(pipelineStages[index + 1], index + 1).active) 
                            ? '#1d4ed8' : '#9ca3af'
                        }
                      />
                    </div>
                  )}
                </React.Fragment>
              );
            })}
          </div>

          {isLastStage && (
            <button
              style={styles.markCompleteButton}
              onClick={() => handleStageChange('Closed')}
            >
              <CheckCircle size={11} />
              Mark Complete
            </button>
          )}
        </div>
      </div>
    );
  };

