                    <div style={{marginBottom: '16px'}}>
                      <div style={{display: 'flex', justifyContent: 'space-between', marginBottom: '4px'}}>
                        <span style={{fontSize: '14px', color: '#374151'}}>Response Time</span>
                        <span style={{fontSize: '14px', fontWeight: '600', color: '#1f2937'}}>2.4h</span>
                      </div>
                      <div style={{
                        width: '100%',
                        height: '8px',
                        backgroundColor: '#e5e7eb',
                        borderRadius: '4px',
                        overflow: 'hidden',
                      }}>
                        <div style={{
                          width: '60%',
                          height: '100%',
                          backgroundColor: '#f59e0b',
                          borderRadius: '4px',
                        }}></div>
                      </div>
                    </div>
                    <div>
                      <div style={{display: 'flex', justifyContent: 'space-between', marginBottom: '4px'}}>
                        <span style={{fontSize: '14px', color: '#374151'}}>Conversion Probability</span>
                        <span style={{fontSize: '14px', fontWeight: '600', color: '#1f2937'}}>68%</span>
                      </div>
                      <div style={{
                        width: '100%',
                        height: '8px',
                        backgroundColor: '#e5e7eb',
                        borderRadius: '4px',
                        overflow: 'hidden',
                      }}>
                        <div style={{
                          width: '68%',
                          height: '100%',
                          backgroundColor: '#3b82f6',
                          borderRadius: '4px',
                        }}></div>
                      </div>
                    </div>
                  </div>

                  {/* Lead Details Card */}
                  <div style={{
                    backgroundColor: 'white',
                    borderRadius: '8px',
                    border: '1px solid #e5e7eb',
                    padding: '20px',
                  }}>
                    <h3 style={{margin: '0 0 16px 0', fontSize: '16px', fontWeight: '600', color: '#1f2937'}}>
                      Lead Details
                    </h3>
                    <div style={{display: 'flex', flexDirection: 'column', gap: '12px'}}>
                      <div>
                        <label style={{fontSize: '12px', color: '#6b7280', marginBottom: '4px', display: 'block'}}>
                          Status
                        </label>
                        <div style={getStatusStyle(editLeadData?.status || 'New')}>
                          {editLeadData?.status || 'New'}
                        </div>
                      </div>
                      <div>
                        <label style={{fontSize: '12px', color: '#6b7280', marginBottom: '4px', display: 'block'}}>
                          Source
                        </label>
                        <div style={{fontSize: '14px', color: '#1f2937', fontWeight: '500'}}>
                          {editLeadData?.source || 'N/A'}
                        </div>
                      </div>
                      <div>
                        <label style={{fontSize: '12px', color: '#6b7280', marginBottom: '4px', display: 'block'}}>
                          Industry
                        </label>
                        <div style={{fontSize: '14px', color: '#1f2937', fontWeight: '500'}}>
                          {editLeadData?.industry || 'N/A'}
                        </div>
                      </div>
                      <div>
                        <label style={{fontSize: '12px', color: '#6b7280', marginBottom: '4px', display: 'block'}}>
                          Assigned To
                        </label>
                        <div style={{fontSize: '14px', color: '#1f2937', fontWeight: '500'}}>
                          {editLeadData?.assignedTo || 'Unassigned'}
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
              <div style={styles.formActions}>
                <button
                  style={styles.button}
                  onClick={handleSaveLeadChanges}
                >
                  Save Changes
                </button>
              </div>
            </div>
          )}

          {/* Other sections placeholder */}
          {activeSection !== 'dashboard' && activeSection !== 'leads' && (
            <div style={styles.card}>
              <h3 style={styles.cardTitle}>
                {navItems.find(item => item.id === activeSection)?.label} Section
              </h3>
              <p style={{color: '#6b7280', margin: '16px 0'}}>
                This section is ready for your content. You can implement the specific functionality for {' '}
                {navItems.find(item => item.id === activeSection)?.label.toLowerCase()} here.
              </p>
            </div>
          )}
        </div>
      </div>

      {/* New Lead Modal */}
      {showLeadModal && (
        <div style={styles.modal} onClick={() => setShowLeadModal(false)}>
          <div style={styles.modalContent} onClick={(e) => e.stopPropagation()}>
            <div style={styles.modalHeader}>
              <h2 style={styles.modalTitle}>Create New Lead</h2>
              <button
                style={styles.closeButton}
                onClick={() => setShowLeadModal(false)}
              >
                <X size={24} />
              </button>
            </div>

            <form style={styles.form} onSubmit={handleSubmitLead}>
              <div style={styles.formGroup}>
                <label style={styles.label}>Full Name *</label>
                <input
                  type="text"
                  required
                  value={newLead.name}
                  onChange={(e) => setNewLead({...newLead, name: e.target.value})}
                  style={styles.input}
                  placeholder="Enter full name"
                />
              </div>

              <div style={styles.formGroup}>
                <label style={styles.label}>Company *</label>
                <input
                  type="text"
                  required
                  value={newLead.company}
                  onChange={(e) => setNewLead({...newLead, company: e.target.value})}
                  style={styles.input}
                  placeholder="Enter company name"
                />
              </div>

              <div style={styles.formGroup}>
                <label style={styles.label}>Email *</label>
                <input
                  type="email"
                  required
                  value={newLead.email}
                  onChange={(e) => setNewLead({...newLead, email: e.target.value})}
                  style={styles.input}
                  placeholder="Enter email address"
                />
              </div>

              <div style={styles.formGroup}>
                <label style={styles.label}>Phone</label>
                <input
                  type="tel"
                  value={newLead.phone}
                  onChange={(e) => setNewLead({...newLead, phone: e.target.value})}
                  style={styles.input}
                  placeholder="Enter phone number"
                />
              </div>

              <div style={styles.formGroup}>
                <label style={styles.label}>Status</label>
                <select
                  value={newLead.status}
                  onChange={(e) => setNewLead({...newLead, status: e.target.value})}
                  style={styles.select}
                >
                  <option value="New">New</option>
                  <option value="Qualified">Qualified</option>
                  <option value="Contacted">Contacted</option>
                  <option value="Proposal">Proposal</option>
                  <option value="Negotiation">Negotiation</option>
                </select>
              </div>

              <div style={styles.formGroup}>
                <label style={styles.label}>Estimated Value</label>
                <input
                  type="number"
                  value={newLead.value}
                  onChange={(e) => setNewLead({...newLead, value: e.target.value})}
                  style={styles.input}
                  placeholder="Enter estimated value"
                />
              </div>

              <div style={styles.formGroup}>
                <label style={styles.label}>Source</label>
                <select
                  value={newLead.source}
                  onChange={(e) => setNewLead({...newLead, source: e.target.value})}
                  style={styles.select}
                >
                  <option value="">Select source</option>
                  <option value="Website">Website</option>
                  <option value="Referral">Referral</option>
                  <option value="LinkedIn">LinkedIn</option>
                  <option value="Cold Call">Cold Call</option>
                  <option value="Email Campaign">Email Campaign</option>
                  <option value="Trade Show">Trade Show</option>
                </select>
              </div>

              <div style={styles.formActions}>
                <button
                  type="button"
                  style={{...styles.button, ...styles.buttonSecondary}}
                  onClick={() => setShowLeadModal(false)}
                >
                  Cancel
                </button>
                <button type="submit" style={styles.button}>
                  Create New Lead
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Click outside to close dropdown */}
      {showQuickActions && (
        <div 
          style={{
            position: 'fixed',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            zIndex: 999,
          }}
          onClick={() => setShowQuickActions(false)}
        />
      )}
    </div>
  );
};

export default App;
