              </div>
              {!rightSidebarCollapsed && (
                <ChevronDown 
                  size={16} 
                  style={{
                    transform: expandedWidgets.notes ? 'rotate(180deg)' : 'rotate(0deg)',
                    transition: 'transform 0.2s'
