                  <input type="checkbox" checked style={styles.taskCheckbox} />
                  <div style={{textDecoration: 'line-through', color: '#9ca3af'}}>
                    <div>Send follow-up emails</div>
                    <div style={{fontSize: '12px'}}>Completed</div>
                  </div>
