              {!rightSidebarCollapsed && (
                <ChevronDown 
                  size={16} 
                  style={{
                    transform: expandedWidgets.emails ? 'rotate(180deg)' : 'rotate(0deg)',
                    transition: 'transform 0.2s'
                  }}
                />
              )}
            </div>
            {expandedWidgets.emails && !rightSidebarCollapsed && (
              <div style={styles.widgetEmpty}>
